import React, { useState } from "react";
import { IconPlus, IconMinus } from "@tabler/icons-react";
import { Accordion } from "@mantine/core";

const Order = () => {
  const groceries = [
    // ... (same as before)
  ];

  const [openItems, setOpenItems] = useState(groceries.map(() => false));

  const toggleChevronIcon = (index) => {
    setOpenItems((prevOpenItems) =>
      prevOpenItems.map((isOpen, i) => (i === index ? !isOpen : isOpen))
    );
  };

  const items = groceries.map((item, index) => (
    <Accordion.Item key={item.value} value={item.value}>
      <Accordion.Control
        icon={item.emoji}
        onClick={() => toggleChevronIcon(index)}
      >
        {item.value}
      </Accordion.Control>
      <Accordion.Panel>{item.description}</Accordion.Panel>
    </Accordion.Item>
  ));

  const chevronIcons = openItems.map((isOpen, index) =>
    isOpen ? <IconMinus key={index} /> : <IconPlus key={index} />
  );

  return (
    <Accordion chevronPosition="left" chevron={chevronIcons}>
      {items}
    </Accordion>
  );
};

export default Order;
