import React, { useState } from "react";
import {
  Flex,
  Stack,
  Text,
  Container,
  Box,
  Accordion,
  Radio,
  Group,
  Center,
  ColorInput,
  Slider,
  Input,
  Divider,
  Button,
  Checkbox,
  ColorPicker,
} from "@mantine/core";
import {
  IconCircleXFilled,
  IconAlignLeft,
  IconAlignCenter,
  IconAlignJustified,
  IconBold,
  IconUnderline,
  IconItalic,
  IconLetterN,
  IconCirclePlus,
} from "@tabler/icons-react";

const TextTemplate = () => {
  const [value, setValue] = useState(1);
  const [selectedIcon, setSelectedIcon] = useState(null);

  const handleChange = (newValue) => {
    setValue(newValue);
  };

  const handleIncrement = () => {
    const newValue = value + 1;
    if (newValue <= 100) {
      setValue(newValue);
    }
  };

  const handleIconClick = (index, component) => {
    console.log("Clicked icon :", component);
    setSelectedIcon(index);
  };

  const icons = [
    { name: "BaselineDensityMedium", component: <IconBaselineDensityMedium /> },
    { name: "BaselineDensitySmall", component: <IconBaselineDensitySmall /> },
    { name: "AlignCenter", component: <IconAlignCenter /> },
    { name: "AlignJustified", component: <IconAlignJustified /> },
    { name: "AlignRight", component: <IconAlignRight /> },
  ];
  return (
    <Box bg="gray.3">
      <Container
        fluid
        bg="indigo.6"
        style={{
          borderRadius: "5px",
          display: "flex",
          height: "40px",
          alignItems: "center",
          justifyContent: "space-between",
        }}
      >
        <Text
          color="white"
          style={{ display: "inline-block", justifyContent: "flex-center" }}
        >
          Text
        </Text>
        <div style={{ display: "flex", justifyContent: "flex-end" }}>
          <IconCircleXFilled />
        </div>
      </Container>

      <Stack m="xs">
        <Text ml="xs">Title</Text>
        <Input
          ml="xs"
          mr="xs"
          placeholder="Please Enter Title"
          style={{ display: "inline-block" }}
        />
      </Stack>

      <Stack m="xs">
        <Text ml="xs">Background Color</Text>
        <ColorPicker ml="xs" mr="xs" w="22rem" size="lg" format="rgba" />
      </Stack>

      <Stack m="xs">
        <Text ml="xs">Text Color</Text>
        <ColorPicker ml="xs" mr="xs" w="22rem" size="lg" format="rgba" />
      </Stack>

      <Stack m="xs">
        <Text ml="xs">Size</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          <Slider
            my="xs"
            ml="xs"
            labelAlwaysOn
            value={value}
            onChange={handleChange}
            min={0}
            max={100}
            style={{ width: "300px" }}
          />
        </Flex>
      </Stack>

      <Stack m="xs">
        <Text ml="xs">Text Style</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          <Radio.Group>
            <Group>
              <Radio
                value="normal"
                label={<IconLetterN style={{ border: "1px solid #ccc" }} />}
              />
              <Radio
                value="bold"
                label={<IconBold style={{ border: "1px solid #ccc" }} />}
              />
              <Radio
                value="italic"
                label={<IconItalic style={{ border: "1px solid #ccc" }} />}
              />
              <Radio
                value="underline"
                label={<IconUnderline style={{ border: "1px solid #ccc" }} />}
              />
            </Group>
          </Radio.Group>
        </Flex>
      </Stack>

      <Stack m="xs">
        <Text ml="xs">Weight</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
