            max={100}
            style={{ width: "300px" }}
          />
        </Flex>
      </Stack>

      <Stack m="xs">
        <Text ml="xs">Padding</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          <Flex
            mih={50}
            gap="md"
            ml="xs"
            justify="center"
            align="flex-start"
            direction="column"
            wrap="wrap"
          >
            <Text mt="xs">Left</Text>

            <Slider
              labelAlwaysOn
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
            <Text>Right</Text>

            <Slider
              labelAlwaysOn
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
            <Text>Top</Text>

            <Slider
              labelAlwaysOn
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
            <Text>Bottom</Text>

            <Slider
              mb="xs"
              labelAlwaysOn
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
          </Flex>
        </Flex>
      </Stack>

      <Stack m="xs">
        <Text ml="xs">Margin</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          <Flex
            mih={50}
            gap="md"
            ml="xs"
            justify="center"
            align="flex-start"
            direction="column"
            wrap="wrap"
          >
            <Text mt="xs">Left</Text>

            <Slider
              labelAlwaysOn
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
            <Text>Right</Text>

            <Slider
              labelAlwaysOn
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
            <Text>Top</Text>

            <Slider
              labelAlwaysOn
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
            <Text>Bottom</Text>

            <Slider
              mb="xs"
              labelAlwaysOn
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
          </Flex>
        </Flex>
      </Stack>

      <Stack m="xs">
        <Text ml="xs">Back Button Radious</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          <Flex
            mih={50}
            gap="md"
            ml="xs"
            justify="center"
            align="flex-start"
            direction="column"
            wrap="wrap"
          >
            <Text mt="xs">Left</Text>

            <Slider
              labelAlwaysOn
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
            <Text>Right</Text>

            <Slider
              labelAlwaysOn
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
            <Text>Top</Text>

            <Slider
              labelAlwaysOn
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
            <Text>Bottom</Text>

            <Slider
              mb="xs"
              labelAlwaysOn
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
          </Flex>
        </Flex>
      </Stack>
    </Box>
  );
};

export default TextTemplate;
