import { Box, Center, Stack, Container } from "@mantine/core";
import React from "react";
import {
  IconCircleXFilled,
  IconHome2,
  IconUser,
  IconSearch,
  IconMessageCircle,
  IconShoppingBag,
  IconBookmark,
} from "@tabler/icons-react";

const ResponsivePhone = () => {
  return (
    <Stack>
      <Center>
        <Box
          style={{
            border: "2px solid #1b1c1b",
            borderRadius: "8px",
            width: "200px", // Fixed width
            height: "400px", // Fixed height
            backgroundColor: "#ffffff",
            boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.2)",
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <Center>Mobile</Center>
        </Box>
      </Center>
    </Stack>
  );
};

export default ResponsivePhone;
