import React, { useState } from "react";
import {
  Flex,
  Stack,
  Text,
  Container,
  Box,
  ActionIcon,
} from "@mantine/core";
import {
  IconCircleXFilled,
  IconAlignLeft,
  IconAlignCenter,
  IconAlignJustified,
  IconAlignRight,
  IconAdjustmentsHorizontal,
  IconBaselineDensityMedium,
  IconBaselineDensitySmall,
} from "@tabler/icons-react";

const Drawer = () => {
  const [value, setValue] = useState(1);
  const [selectedIcon, setSelectedIcon] = useState(null);

  const handleChange = (newValue) => {
    setValue(newValue);
  };

  const handleIncrement = () => {
    const newValue = value + 1;
    if (newValue <= 100) {
      setValue(newValue);
    }
  };

  const handleIconClick = (index) => {
    // Update the selectedIcon state to the clicked icon's index
    setSelectedIcon(index);
  };

  // Create an array of icons to render
  const icons = [
    <IconBaselineDensityMedium style={{ border: "1px solid #ccc" }} />,
    <IconBaselineDensitySmall style={{ border: "1px solid #ccc" }} />,
    <IconAlignCenter style={{ border: "1px solid #ccc" }} />,
    <IconAlignJustified style={{ border: "1px solid #ccc" }} />,
    <IconAlignRight style={{ border: "1px solid #ccc" }} />,
  ];

  return (
    <Box bg="gray.3">
      <Container
        fluid
        bg="indigo.6"
        style={{
          borderRadius: "5px",
          display: "flex",
          height: "40px",
          alignItems: "center",
          justifyContent: "space-between",
        }}
      >
        <Text
          color="white"
          style={{ display: "inline-block", justifyContent: "flex-center" }}
        >
          Drawer Icon
        </Text>
        <div style={{ display: "flex", justifyContent: "flex-end" }}>
          <IconCircleXFilled />
        </div>
      </Container>

      <Stack m="xs">
        <Text ml="xs">Change Icon</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          {/* Map over the array of icons and render them as ActionIcons */}
          {icons.map((icon, index) => (
            <ActionIcon
              key={index}
              variant={selectedIcon === index ? "outline" : "default"}
              aria-label={`Icon ${index}`}
              onClick={() => handleIconClick(index)} // Toggle the variant on click
            >
              {icon}
            </ActionIcon>
          ))}
        </Flex>
      </Stack>
    </Box>
  );
};

export default Drawer;
