import React, { useState } from "react";
import {
  Box,
  Text,
  Flex,
  Accordion,
  Center,
  Avatar,
  Badge,
  Checkbox,
} from "@mantine/core";

const BrandList = (props) => {
  const brandSections = [
    {
      title: "Electronics",
      color: "yellow",
      iconSrc:
        "https://blogassets.leverageedu.com/blog/wp-content/uploads/2020/01/02232228/BSc-Electronics.png",
      iconCheckVisible: false,
      onClick: () => toggleSection(0),
    },
    {
      title: "Accessories",
      color: "yellow",
      iconSrc:
        "https://hnsfpau.imgix.net/5/images/detailed/240/TH-32JS610Z.01.jpg?fit=fill&bg=0FFF&w=1500&h=844&auto=format,compress",
      iconCheckVisible: false,
      onClick: () => toggleSection(1),
    },
    {
      title: "Clothing",
      color: "yellow",
      iconSrc:
        "https://i0.wp.com/post.healthline.com/wp-content/uploads/2020/07/Best_Baby_Clothes_1296x728.png?w=1155&h=1528",
      iconCheckVisible: false,
      onClick: () => toggleSection(2),
    },
    {
      title: "Shoes",
      color: "yellow",
      iconSrc:
        "https://media.gq.com/photos/63eba1b2275d2fef78a425c2/16:9/w_2560%2Cc_limit/nike-running-shoes-streakfly-invincible.jpg",
      iconCheckVisible: false,
      onClick: () => toggleSection(3),
    },
  ];

  const [boxVisible, setBoxVisible] = useState(Array(brandSections.length).fill(false));

  const toggleSection = (index) => {
    const updatedVisibility = boxVisible.map((value, i) => i === index ? !value : false);
    setBoxVisible(updatedVisibility);
  };

  return (
    <Accordion.Item value="brand-list">
      <Accordion.Control bg="indigo.2">
        <Center>
          <Text fw={700}>Brand List</Text>
        </Center>
      </Accordion.Control>
      <Accordion.Panel>
        {brandSections.map((section, index) => (
          <Box
            key={index}
            mb="xs"
            bg="#FFFFFF"
            style={{ cursor: "pointer" }}
            onClick={section.onClick}
          >
            <Flex
              h={{ base: "auto", md: 100 }}
              justify="center"
              align="center"
              direction={{ base: "column", md: "row" }}
              gap="sm"
            >
              <Flex justify="center" align="center" direction="column">
                <Avatar
                  variant="filled"
                  radius="sm"
                  size="lg"
                  color={section.color}
                  src={section.iconSrc}
                />
                <Text size="xs">{section.title}</Text>
              </Flex>
              {section.iconCheckVisible && (
                <Flex h={{ base: "auto", md: `100%` }}>
                  <Checkbox defaultChecked />
                </Flex>
              )}
            </Flex>
          </Box>
        ))}
      </Accordion.Panel>
    </Accordion.Item>
  );
};

export default BrandList;
