import React, { useState } from "react";
import {
  Text,
  Box,
  Accordion,
  Flex,
  ColorPicker,
  Input,
  Stack,
  Slider,
} from "@mantine/core";
import { IconPlus } from "@tabler/icons-react";

const CategoryCustomize = ({ customizations, setCustomizations }) => {
  const handleRadiusChange = (newValue) => {
    setCustomizations({ ...customizations, boxRadius: newValue });
  };

  const handleRadiusIncrement = () => {
    const newValue = customizations.boxRadius + 1;
    if (newValue <= 100) {
      setCustomizations({ ...customizations, boxRadius: newValue });
    }
  };

  const handlePaddingChange = (newValue) => {
    setCustomizations({ ...customizations, padding: newValue });
  };

  const handlePaddingIncrement = () => {
    const newValue = customizations.padding + 1;
    if (newValue <= 100) {
      setCustomizations({ ...customizations, padding: newValue });
    }
  };

  const handleMarginChange = (newValue) => {
    setCustomizations({ ...customizations, margin: newValue });
  };

  const handleMarginIncrement = () => {
    const newValue = customizations.margin + 1;
    if (newValue <= 100) {
      setCustomizations({ ...customizations, margin: newValue });
    }
  };

  return (
    <Box
      m="xs"
      h="470px"
      bg="gray.2"
      style={{ borderRadius: "10px", overflowY: "auto" }}
    >
      {/* ... (previous code) */}
      {/* Box Radius */}
      <Text size="sm">Box Radius</Text>
      <Flex
        mih={40}
        bg="white"
        gap="xs"
        justify="center"
        align="center"
        direction="row"
        wrap="wrap"
        style={{
          border: "1px solid #1b1c1b",
          borderRadius: "5px",
        }}
      >
        <Input
          type="number"
          size="xs"
          value={customizations.boxRadius}
          w={45}
          readOnly
          style={{ borderRadius: "20px" }}
        />
        <Slider
          value={customizations.boxRadius}
          onChange={handleRadiusChange}
          min={0}
          max={100}
          w={160}
          label={null}
        />
        <IconPlus
          onClick={handleRadiusIncrement}
          style={{
            border: "1px solid #000",
            borderRadius: "50%",
            cursor: "pointer",
          }}
        />
      </Flex>
      {/* ... (previous code) */}
    </Box>
  );
};

export default CategoryCustomize;
