import React from "react";
import {
  Box,
  Grid,
  Text,
  Flex,
  Accordion,
  Center,
  Avatar,
  Badge,
  Checkbox,
  Button,
  ColorPicker,
  Input,
  Stack,
  Image,
  Card,
  Slider,
  ActionIcon,
} from "@mantine/core";

import {
  IconPlus,
  IconX,
  IconColumns1,
  IconBold,
  IconUnderline,
  IconItalic,
  IconLetterN,
} from "@tabler/icons-react";

const Customize = () => {
  return (
    <Box m="xs" h={`fit-content`} bg="gray.2" style={{ borderRadius: "10px" }}>
      <Box
        bg="indigo.5"
        ml="xs"
        mr="xs"
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          backgroundColor: "indigo.5",
          padding: "10px",
          borderRadius: "10px",
        }}
      >
        <Text
          fw={900}
          c={`#FFFFFF`}
          style={{
            flex: "1",
            textAlign: "center",
          }}
        >
          Customize
        </Text>
        <IconX
          size={28}
          style={{
            borderRadius: "50%",
            background: "red",
            color: "white",
            alignSelf: "flex-end",
          }}
        />
      </Box>

      {/* Category Item */}
      <Accordion
        m="xs"
        variant="contained"
        defaultValue="category-item"
        pb={10}
      ></Accordion>
    </Box>
  );
};

export default Customize;
