import React from "react";
import { useDisclosure } from "@mantine/hooks";
import {
  AppShell,
  Box,
  Burger,
  Button,
  Group,
  Skeleton,
  Autocomplete,
  Center,
} from "@mantine/core";
import {
  IconAlignLeft,
  IconSearch,
  IconBellFilled,
  IconX,
} from "@tabler/icons-react";
import NavbarMinimal from "./NavbarMinimal";

const AppShellDemo = () => {
  const [opened, { toggle }] = useDisclosure();
  return (
    <AppShell
      header={{ height: 50 }}
      navbar={{ width: 80, breakpoint: "sm", collapsed: { mobile: !opened } }}
      aside={{
        width: 300,
        breakpoint: "md",
        collapsed: { desktop: false, mobile: true },
      }}
      padding="md"
    >
      <AppShell.Header>
        <Group h="100%" px="md">
          <Burger opened={opened} onClick={toggle} hiddenFrom="sm" size="sm" />
          {/* <MantineLogo size={30} /> */}
        </Group>
      </AppShell.Header>
      <AppShell.Navbar p="md">
        {/* Navbar
        {Array(15)
          .fill(0)
          .map((_, index) => (
            <Skeleton key={index} h={28} mt="sm" animate={false} />
          ))} */}
          <NavbarMinimal />
      </AppShell.Navbar>
      <AppShell.Main>
        Aside is hidden on on md breakpoint and cannot be opened when it is
        collapsed
      </AppShell.Main>
      <AppShell.Aside p="md">
        <Button fullWidth>Item</Button>
        <Box my="xs" maw={400} mx="auto">
          <div className="flex justify-between">
            <IconAlignLeft className="mt-2 inline-block" />{" "}
            {/* Add inline-block class */}
            <Autocomplete
              placeholder="Search"
              data={[
                "React",
                "Angular",
                "Vue",
                "Next.js",
                "Riot.js",
                "Svelte",
                "Blitz.js",
              ]}
            />
            <div className="flex justify-between inline-block">
              {" "}
              {/* Add inline-block class */}
              <IconSearch className="mt-2 mr-1 inline-block" />{" "}
              {/* Add inline-block class */}
              <div className="inline-block">
                <IconBellFilled className="mt-2" />
              </div>
            </div>
          </div>
        </Box>
      </AppShell.Aside>
    </AppShell>
  );
};

export default AppShellDemo;
