import {
  Box,
  Text,
  Flex,
  Accordion,
  Center,
  Avatar,
  Checkbox,
  Grid,
  ColorPicker,
  Input,
  Image,
} from "@mantine/core";
import React, { useState } from "react";
import { Dropzone, IMAGE_MIME_TYPE } from "@mantine/dropzone";
import ResponsivePhone from "../ResponsivePhone";

const categories = [
  {
    name: "Electronics",
    imageSrc:
      "https://blogassets.leverageedu.com/blog/wp-content/uploads/2020/01/02232228/BSc-Electronics.png",
  },
  {
    name: "Accessories",
    imageSrc:
      "https://hnsfpau.imgix.net/5/images/detailed/240/TH-32JS610Z.01.jpg?fit=fill&bg=0FFF&w=1500&h=844&auto=format,compress",
  },
  {
    name: "Clothing",
    imageSrc:
      "https://i0.wp.com/post.healthline.com/wp-content/uploads/2020/07/Best_Baby_Clothes_1296x728.png?w=1155&h=1528",
  },
  {
    name: "Shoes",
    imageSrc:
      "https://media.gq.com/photos/63eba1b2275d2fef78a425c2/16:9/w_2560%2Cc_limit/nike-running-shoes-streakfly-invincible.jpg",
  },
];

const Index = () => {
  const [flexBlock, setFlexBlock] = useState(null);
  const [showCustomization, setShowCustomization] = useState(false);
  const [inputValue, setInputValue] = useState("");
  const [filesStateElectronics, setFilesStateElectronics] = useState([]);

  const handleCategoryClick = (categoryName) => {
    if (categoryName === "Electronics") {
      setShowCustomization(true);
      setInputValue("Electronics"); // Set text content for Electronics
    } else {
      setShowCustomization(false);
    }
    updateFlexBlock(inputValue, categoryName); // Pass inputValue as text content
  };

  const updateFlexBlock = (inputValue, categoryName) => {
    const updatedCategories = categories.map((category) => {
      if (category.name === categoryName) {
        return {
          ...category,
          name: inputValue, // Set text content based on inputValue
          imageSrc:
            categoryName === "Electronics" && filesStateElectronics.length > 0
              ? URL.createObjectURL(filesStateElectronics[0])
              : category.imageSrc,
        };
      }
      return category;
    });

    setFlexBlock(
      <Flex
        h="70px"
        mt="5px"
        justify="center"
        align="center"
        direction={{ base: "column", md: "row" }}
        gap="xs"
      >
        {updatedCategories.map((category, i) => (
          <Flex
            key={i}
            justify="center"
            align="center"
            direction="column"
            gap="xs"
          >
            <Avatar
              variant="filled"
              radius="sm"
              size="md"
              color="yellow"
              src={category.imageSrc}
            />
            <Text
              color={categoryName === "Electronics" ? "blue" : "black"} // Set text color
              size="0.5rem"
            >
              {category.name}
            </Text>
          </Flex>
        ))}
      </Flex>
    );
  };

  // ... (rest of the component code)

  return (
    <Grid mt="xs" pb="xs">
      <Grid.Col span={4}>
        <Accordion>
          <Accordion.Item value="category-list">
            <Accordion.Control bg="indigo.2">
              <Center>
                <Text fw={700}>Category List</Text>
              </Center>
            </Accordion.Control>
            <Accordion.Panel bg="gray.2">
              <Box mb="xs" bg="#FFFFFF" style={{ cursor: "pointer" }}>
                <Flex
                  h={{ base: "auto", md: 100 }}
                  justify="center"
                  align="center"
                  direction={{ base: "column", md: "row" }}
                  gap="sm"
                >
                  {categories.map((category, index) => (
                    <Flex
                      key={index}
                      justify="center"
                      align="center"
                      direction="column"
                      onClick={() => handleCategoryClick(category.name)}
                    >
                      <Avatar
                        variant="filled"
                        radius="sm"
                        size="lg"
                        color="yellow"
                        src={category.imageSrc}
                      />
                      <Text size="xs">{category.name}</Text>
                    </Flex>
                  ))}
                  <Flex h={{ base: "auto", md: `100%` }}>
                    <Checkbox defaultChecked />
                  </Flex>
                </Flex>
              </Box>
            </Accordion.Panel>
          </Accordion.Item>
        </Accordion>
      </Grid.Col>
      <Grid.Col span={4}>
        <ResponsivePhone flexBlock={flexBlock} />
      </Grid.Col>
      <Grid.Col span={4}>
        {showCustomization && (
          <Accordion
            m="xs"
            variant="contained"
            defaultValue="category-item"
            pb={10}
          >
            <Accordion.Item value="category-item">
              <Accordion.Control bg="#FFFFFF">
                <Text>Category Item</Text>
              </Accordion.Control>
              <Accordion.Panel bg="#FFFFFF">
                <Accordion m="xs" variant="contained">
                  <Accordion.Item value="electronics">
                    <Accordion.Control bg="gray.2" direction="row">
                      <Flex
                        justify="flex-start"
                        align="center"
                        direction="row"
                        gap="md"
                      >
                        <Checkbox
                          defaultChecked
                          onClick={(event) => {
                            event.stopPropagation(); // Prevent accordion from opening
                          }}
                        />
                        <Text>Electronics</Text>
                      </Flex>
                    </Accordion.Control>
                    <Accordion.Panel>
                      <Dropzone
                        accept={IMAGE_MIME_TYPE}
                        onDrop={(files) => onImageDrop(files)}
                        bg="white"
                        h="13rem"
                        style={{
                          position: "relative",
                          border: "1px solid #1b1c1b",
                          borderRadius: "5px",
                          display: "flex",
                          alignItems: "center",
                          justifyContent:
                            previews("Electronics")?.length === 0
                              ? "center"
                              : "center",
                        }}
                      >
                        {previews("Electronics")?.length === 0 ? (
                          <Text
                            style={{
                              position: "absolute",
                              top: "50%",
                              left: "50%",
                              transform: "translate(-50%, -50%)",
                            }}
                          >
                            Drop images here
                          </Text>
                        ) : null}
                        {previews("Electronics")}
                      </Dropzone>
                      <Flex direction="column">
                        <Text>Background Color</Text>
                        <ColorPicker
                          w="auto"
                          size="lg"
                          format="rgba"
                          value={backgroundColor}
                          onChange={handleColorChange}
                        />
                      </Flex>
                      <Flex direction="column">
                        <Text>Level</Text>
                        <Input
                          placeholder="Category name"
                          size="xs"
                          value={inputValue}
                          onChange={handleInputChange}
                        />
                      </Flex>
                    </Accordion.Panel>
                  </Accordion.Item>
                </Accordion>
              </Accordion.Panel>
            </Accordion.Item>
          </Accordion>
        )}
      </Grid.Col>
    </Grid>
  );
};

export default Index;
