import React from "react";
import { Grid } from "@mantine/core";
import ImageTest from "./ImageTest";
import TextTemplate from "./TextTemplate";
import Drawer from "./Drawer";
import Navbar from "./Navbar";
import NavbarItem from "./NavbarItem";
import ResponsivePhone from "./ResponsivePhone";
import AppBar from "../Global/AppBar";
import AccordionBar from "./AccordionBar";
import { useDisclosure } from "@mantine/hooks"; // Import useDisclosure

const Index = () => {
  // Initialize useDisclosure to manage the openDrawer state
  const openDrawerDisclosure = useDisclosure();

  return (
    <Grid>
      <Grid.Col span={4}>
        <AccordionBar setOpenDrawer={openDrawerDisclosure.toggle} />
      </Grid.Col>
      <Grid.Col span={4}>
        <ResponsivePhone
          openDrawer={openDrawerDisclosure.isOpen}
          setOpenDrawer={openDrawerDisclosure.onToggle}
        />
      </Grid.Col>
      <Grid.Col span={4}><Navbar /></Grid.Col>
      <Grid.Col span={4}><NavbarItem /></Grid.Col>
      <Grid.Col span={4}><AppBar /></Grid.Col>
      <Grid.Col span={4}>
        <ImageTest />
      </Grid.Col>
      <Grid.Col span={4}>
        <TextTemplate />
      </Grid.Col>
      <Grid.Col span={4}>
        <Drawer />
      </Grid.Col>
    </Grid>
  );
};

export default Index;
