import React from "react";
import { useDisclosure, Text, AppShell, Burger, Group, Button, Dropdown, Paper, Text as MantineText } from "@mantine/core";
import { FiHome, FiSettings, FiUsers, FiMail, FiCalendar, FiHelpCircle, FiCreditCard, FiLogOut } from "react-icons/fi"; // Import icons from Feather Icons

const AppShellDemo = () => {
  const [opened, { toggle }] = useDisclosure();

  const headerStyles = {
    backgroundColor: "blue", // Set the background color to blue
    borderRadius: "0.5rem", // Apply rounded corners
    padding: "0.5rem", // Add padding to the header
  };

  // Data for navigation items
  const navItems = [
    { label: "Home", icon: <FiHome />, dropdown: ["Dashboard", "Profile", "Messages"] },
    { label: "Settings", icon: <FiSettings />, dropdown: ["General", "Security", "Notifications"] },
    { label: "Users", icon: <FiUsers />, dropdown: ["All Users", "Active Users", "Inactive Users"] },
    { label: "Mail", icon: <FiMail />, dropdown: ["Inbox", "Sent", "Drafts"] },
    { label: "Calendar", icon: <FiCalendar />, dropdown: ["Events", "Meetings", "Holidays"] },
    { label: "Help", icon: <FiHelpCircle />, dropdown: ["FAQ", "Support", "Contact Us"] },
    { label: "Billing", icon: <FiCreditCard />, dropdown: ["Payment History", "Invoices", "Billing Info"] },
    { label: "Logout", icon: <FiLogOut />, dropdown: [] }, // No dropdown for the Logout item
  ];

  return (
    <AppShell header={{ height: 60 }} padding="md">
      <div style={{ backgroundColor: "blue" }} className="rounded-lg p-2">
        <AppShell.Header style={headerStyles}>
          <Group h="50%" px="md">
            <Text>Them-1</Text>
          </Group>
          <Group h="50%" px="md">
            <Text>Test</Text>
          </Group>

          {/* Burger */}
          <Burger opened={opened} onClick={toggle} hiddenFrom="sm" size="sm" />
        </AppShell.Header>
        <Group h="50%" px="md">
          {navItems.map((item, index) => (
            <Dropdown
              key={index}
              control={<Button size="sm" variant="light" leftIcon={item.icon}>{item.label}</Button>}
              position="bottom-start"
              arrowSize={5}
              arrowStyle={{ backgroundColor: "blue" }}
              withArrow
              zIndex={1000}
              gutter={10}
            >
              <Paper padding="sm">
                <ul>
                  {item.dropdown.map((subitem, subindex) => (
                    <li key={subindex}>
                      <MantineText>{subitem}</MantineText>
                    </li>
                  ))}
                </ul>
              </Paper>
            </Dropdown>
          ))}
        </Group>
      </div>
    </AppShell>
  );
};

export default AppShellDemo;
