import React, { useState } from "react";
import {
  Box,
  Text,
  Flex,
  Accordion,
  Center,
  Badge,
  Checkbox,
  Image,
  Card,
} from "@mantine/core";

const categoryData = [
  {
    title: "Category 1",
    products: [
      {
        name: "Dell",
        price: "$29,999",
        image:
          "https://johnlewis.scene7.com/is/image/JohnLewis/laptop-carousel1-010323",
      },
      {
        name: "Dell",
        price: "$29,999",
        image:
          "https://sonysmart.com.bd/public/uploads/all/wmAR0iWAr5pRubmvbCtS0bXtrzqhpTCD2nUxSaDN.jpg",
      },
      {
        name: "Dell",
        price: "$29,999",
        image:
          "https://images.macrumors.com/t/RnswrVxVO5LlEOaPBJfTaCue5iU=/1200x1200/smart/article-new/2023/02/airpods-pro-2-red-pink.jpg",
      },
    ],
  },
  {
    title: "Category 2",
    products: [
      {
        name: "Dell",
        price: "$29,999",
        image:
          "https://johnlewis.scene7.com/is/image/JohnLewis/laptop-carousel1-010323",
      },
      {
        name: "Dell",
        price: "$29,999",
        image:
          "https://sonysmart.com.bd/public/uploads/all/wmAR0iWAr5pRubmvbCtS0bXtrzqhpTCD2nUxSaDN.jpg",
      },
      
    ],
  },
];

const CategoryWithProduct = () => {
  const [boxVisible, setBoxVisible] = useState(Array(categoryData.length).fill(false));
  const [iconCheckVisible, setIconCheckVisible] = useState(Array(categoryData.length).fill(false));

  const handleCategoryWithProductClick = (index) => {
    const updatedBoxVisible = boxVisible.map((_, i) => i === index);
    const updatedIconCheckVisible = iconCheckVisible.map((_, i) => i === index);
    setBoxVisible(updatedBoxVisible);
    setIconCheckVisible(updatedIconCheckVisible);
  };

  return (
    <Accordion.Item value="category-with-product">
      <Accordion.Control bg="indigo.2">
        <Center>
          <Text fw={700}>Category With Product</Text>
        </Center>
      </Accordion.Control>
      <Accordion.Panel>
        {categoryData.map((category, index) => (
          <Box
            key={category.title}
            bg="gray.2"
            style={{ cursor: "pointer" }}
            onClick={() => handleCategoryWithProductClick(index)}
          >
            <Flex justify="space-between" align="center" direction="row">
              <Text ml="xs">{category.title}</Text>
              {iconCheckVisible[index] && (
                <Flex mr="xs" h={{ base: "auto", md: `100%` }}>
                  <Checkbox defaultChecked />
                </Flex>
              )}
            </Flex>
            <Flex
              gap="2px"
              justify="flex-start"
              align="center"
              direction="row"
              wrap="wrap"
            >
              {category.products.map((product, productIndex) => (
                <Card
                  key={productIndex}
                  ml="xs"
                  shadow="sm"
                  padding="xs"
                  radius="md"
                  w={90}
                >
                  <Badge color="pink" variant="light" mb="xs">
                    On Sale
                  </Badge>
                  <Card.Section>
                    <Image
                      ml="xs"
                      mr="xs"
                      radius="md"
                      h={100}
                      w={70}
                      fit="contain"
                      src={product.image}
                      alt="Product"
                    />
                  </Card.Section>
                  <Text fw={500}>{product.name}</Text>
                  <Text fw={500}>{product.price}</Text>
                </Card>
              ))} 
            </Flex>
          </Box>
        ))}
      </Accordion.Panel>
    </Accordion.Item>
  );
};

export default CategoryWithProduct;
