                              wrap="wrap"
                              style={{
                                border: "1px solid #1b1c1b",
                                borderRadius: "5px",
                              }}
                            >
                              {icons.map((icon, index) => (
                                <ActionIcon
                                  key={index}
                                  variant={
                                    selectedIcon === index
                                      ? "outline"
                                      : "default"
                                  }
                                  aria-label={`Icon ${icon.component}`}
                                  onClick={() => handleIconClick(index, icon)}
                                >
                                  {icon.component}
                                </ActionIcon>
                              ))}
                            </Flex>
                            <Stack
                              m="xs"
                              bg="white"
                              style={{
                                border: "1px solid #1b1c1b",
                                borderRadius: "5px",
                              }}
                            >
                              <Text ml="xs">Text Content</Text>
                              <Text
                                ml="xs"
                                style={
                                  textStyles[
                                    icons[selectedIcon]?.style || "normal"
                                  ]
                                }
                              >
                                Your text content goes here.
                              </Text>
                            </Stack>
                          </Stack>
                        </Stack>
                      )}
                    </Stack>
                    {/* Add Cart Icon */}
                    <Flex
                      justify="space-between"
                      align="center"
                      direction="row"
                      wrap="wrap"
                    >
                      <Text>Add Cart Icon</Text>
                      <Checkbox defaultChecked />
                    </Flex>
                    {/* Offer */}
                    <Flex
                      justify="space-between"
                      align="center"
                      direction="row"
                      wrap="wrap"
                    >
                      <Text>Offer</Text>
                      <Checkbox defaultChecked />
                    </Flex>
                    {/* Favorite Icon */}
                    <Flex
                      justify="space-between"
                      align="center"
                      direction="row"
                      wrap="wrap"
                    >
                      <Text>Favorite Icon</Text>
                      <Checkbox defaultChecked />
                    </Flex>
                    {/* Ratting */}
                    <Flex
                      justify="space-between"
                      align="center"
                      direction="row"
                      wrap="wrap"
                    >
                      <Text>Ratting</Text>
                      <Checkbox defaultChecked />
                    </Flex>
                  </Stack>
                </Accordion.Panel>
              </Accordion.Item>
            </Accordion>
          </Box>
        )}
      </Grid.Col>
    </Grid>
  );
};

export default Home;
