import { Box, Center, Flex, Grid, Stack, Text } from "@mantine/core";
import React from "react";

const Layout3 = () => {
  const items = ["Item 1", "Item 2", "Item 3"];

  // Define the object that represents the JSX structure
  const limeBoxJSX = {
    type: Box,
    props: {
      bg: "lime.5",
      style: { borderRadius: "10px 10px 0 0", cursor: "pointer" },
    },
    children: [
      {
        type: Center,
        children: {
          type: Text,
          children: "Test",
        },
      },
    ],
  };

  // Function to recursively render the JSX structure from the object
  const renderJSX = (jsxObject) => {
    const { type, props, children } = jsxObject;
    return React.createElement(
      type,
      props,
      children.map((child) => {
        if (child.children) {
          return renderJSX(child);
        }
        return child;
      })
    );
  };

  return (
    <Box bg="gray.4" h={200} style={{ borderRadius: "10px" }}>
      <Stack>
        {/* Render the lime-colored box using the defined object */}
        {renderJSX(limeBoxJSX)}
        <Grid ml="xs" mr="xs">
          {items.map((item) => (
            <Grid.Col span={4}>
              <Box
                bg="blue.1"
                h={60}
                style={{ border: "1px solid #000000", cursor: "pointer" }}
              >
                <Center>{item}</Center>
              </Box>
            </Grid.Col>
          ))}
        </Grid>
      </Stack>
    </Box>
  );
}

export default Layout3;
