import React, { useState } from "react";
import {
  Box,
  Center,
  Stack,
  Flex,
  ActionIcon,
  Autocomplete,
  rem,
  Avatar,
} from "@mantine/core";
import {
  IconHome2,
  IconUser,
  IconMessageCircle,
  IconBookmark,
  IconAlignLeft,
  IconSearch,
  IconCircleXFilled,
  IconBell,
} from "@tabler/icons-react";

const ResponsivePhone = () => {
  const [selectedBlackIcon, setSelectedBlackIcon] = useState(null);

  const handleBlackIconClick = (index, component) => {
    console.log("Clicked icon :", component);
    setSelectedBlackIcon(index);
  };

  const blackicons = [
    { name: "IconHome2", component: <IconHome2 /> },
    { name: "IconMessageCircle", component: <IconMessageCircle /> },
    { name: "IconBookmark", component: <IconBookmark /> },
    { name: "IconUser", component: <IconUser /> },
  ];

  return (
    <Stack>
      <Center>
        <Box
          style={{
            position: "relative", // Make the main box a relative container
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            border: "2px solid #1b1c1b",
            borderRadius: "15px",
            width: "200px", // Fixed width
            height: "400px", // Fixed height
            backgroundColor: "#ffffff",
            boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.2)",
          }}
        >
          {/* Top Box */}
          <Box
            bg="dark.9"
            style={{
              width: "200px",
              height: "30px",
              border: "2px solid #000",
              borderRadius: "15px 15px 0px 0px",
            }}
          >
            <Center mt="xs">
              <Box
                bg="white"
                style={{
                  width: "50px",
                  height: "5px",
                  border: "2px solid #000",
                  borderRadius: "15px 15px 15px 15px",
                }}
              />
            </Center>
          </Box>

          {/* Center Box (Takes the remaining space) */}
          <Box
            bg="light.2"
            style={{
              flex: 1,
              width: "100%",
              border: "2px solid #000",
              marginTop: "-2px",
              marginBottom: "-2px",
              display: "flex",
              flexDirection: "column", // Arrange items in a column
            }}
          >
            <div style={{ flex: 1, background: "white", height: "20px" }}>
              Row 1
            </div>
            <div style={{ flex: 2, background: "lightgray" }}>Row 3</div>
            <div style={{ flex: 1, background: "gray", height: "20px" }}>
              Row 2
            </div>
          </Box>

          {/* Bottom Box (Positioned at the bottom) */}
          <Box
            bg="dark.9"
            style={{
              width: "200px",
              height: "30px",
              border: "2px solid #000",
              borderRadius: "0px 0px 15px 15px",
            }}
          >
            <Center mt="xs">
              <Box
                bg="white"
                style={{
                  width: "50px",
                  height: "5px",
                  border: "2px solid #000",
                  borderRadius: "15px 15px 15px 15px",
                }}
              />
            </Center>
          </Box>
        </Box>
      </Center>
    </Stack>
  );
};

export default ResponsivePhone;
