import { Stack, Text, ColorPicker, Flex, Slider, Input } from "@mantine/core";
import React, { useState } from "react";

const Layout3 = ({ onColorChange, boxBorderRadius, onBorderRadiusChange, textBgColor, onTextBgColorChange, textBorderRadius, onTextBorderRadiusChange }) => {
  const [value, setValue] = useState(1);
  const [text, setText] = useState("Test");

  const handleChange = (newValue) => {
    setValue(newValue);
  };

  const handleTextChange = (newText) => {
    setText(newText);
  };

  return (
    <Stack bg="gray.3">
      <Stack>
        <Text style={{ marginLeft: "1rem" }}>Background Color</Text>
        <ColorPicker
          ml="xs"
          mr="xs"
          mb="xs"
          w="auto"
          size="lg"
          format="rgba"
          onChange={(color) => {
            onColorChange(color);
            onTextBgColorChange(color); // Update the text box's background color
          }}
        />
      </Stack>
      <Stack>
        <Text style={{ marginLeft: "1rem" }}>Box Shadow</Text>
        <Input
          type="text"
          value={boxBorderRadius}
          onChange={(event) => {
            onBorderRadiusChange(event.target.value);
          }}
          ml="xs"
          mr="xs"
          mb="xs"
        />
      </Stack>
      <Stack>
        <Text style={{ marginLeft: "1rem" }}>Text</Text>
        <Input
          type="text"
          value={text}
          onChange={(event) => {
            handleTextChange(event.target.value);
          }}
          ml="xs"
          mr="xs"
          mb="xs"
        />
      </Stack>
      
    </Stack>
  );
};

export default Layout3;
