import React, { useState } from 'react';
import { SegmentedControl, Text } from '@mantine/core';
import {
  IconShoppingCart,
  IconLicense,
  IconMessage2,
  IconBellRinging,
  IconMessages,
  IconFingerprint,
  IconKey,
  IconSettings,
  Icon2fa,
  IconUsers,
  IconFileAnalytics,
  IconDatabaseImport,
  IconReceipt2,
  IconReceiptRefund,
  IconLogout,
  IconSwitchHorizontal,
} from '@tabler/icons-react';
import classes from './NavbarSegmented.module.css';

const tabs = {
  account: [
    { link: '', label: 'Notifications', icon: IconBellRinging },
    { link: '', label: 'Billing', icon: IconReceipt2 },
    { link: '', label: 'Security', icon: IconFingerprint },
    { link: '', label: 'SSH Keys', icon: IconKey },
    { link: '', label: 'Databases', icon: IconDatabaseImport },
    { link: '', label: 'Authentication', icon: Icon2fa },
    { link: '', label: 'Other Settings', icon: IconSettings },
  ],
  general: [
    { link: '', label: 'Orders', icon: IconShoppingCart },
    { link: '', label: 'Receipts', icon: IconLicense },
    { link: '', label: 'Reviews', icon: IconMessage2 },
    { link: '', label: 'Messages', icon: IconMessages },
    { link: '', label: 'Customers', icon: IconUsers },
    { link: '', label: 'Refunds', icon: IconReceiptRefund },
    { link: '', label: 'Files', icon: IconFileAnalytics },
  ],
};

const NavbarSegmented = () => {
  const [active, setActive] = useState('account'); // Initialize to 'account' by default

  const handleSegmentedControlChange = (newValue) => {
    setActive(newValue);
  };

  return (
    <div className={classes.container}>
      <nav className={`${classes.navbar} ${classes.navbarMain}`}>
        <SegmentedControl
          transitionTimingFunction="ease"
          fullWidth
          data={[
            { label: 'Account', value: 'account' },
            { label: 'System', value: 'general' },
          ]}
          value={active}
          onChange={handleSegmentedControlChange}
        />
      </nav>
      <div className={classes.content}>
        {active === 'account' && (
          <div>
            {/* Render account-related content here */}
            {tabs.account.map((tab, index) => (
              <a href={tab.link} className={classes.link} key={index}>
                <tab.icon className={classes.linkIcon} /> {tab.label}
              </a>
            ))}
          </div>
        )}
        {active === 'general' && (
          <div>
            {/* Render general-related content here */}
            {tabs.general.map((tab, index) => (
              <a href={tab.link} className={classes.link} key={index}>
                <tab.icon className={classes.linkIcon} /> {tab.label}
              </a>
            ))}
          </div>
        )}
      </div>
      <div className={classes.footer}>
        {/* Footer content */}
      </div>
    </div>
  );
};

export default NavbarSegmented;
