import React, { useState } from "react";
import {
  Box,
  Grid,
  Text,
  Flex,
  Accordion,
  Center,
  Avatar,
  Badge,
  Checkbox,
  Button,
  ColorPicker,
  Input,
  Stack,
  Image,
  Card,
  Slider,
  ActionIcon,
} from "@mantine/core";

import { Dropzone, IMAGE_MIME_TYPE } from "@mantine/dropzone";

const CustomizeCategoryList1 = () => {
  {
    /* Dropzone */
  }

  const [files, setFiles] = useState([]);

  const previews = files.map((file, index) => {
    const imageUrl = URL.createObjectURL(file);
    return (
      <Image
        key={index}
        src={imageUrl}
        onLoad={() => URL.revokeObjectURL(imageUrl)}
      />
    );
  });

  const [categoryHeader, setCategoryHeader] = useState({
    electronic_level: "Electronics",
    accessories_level: "Accessories",
    clothing_level: "Clothing",
    shoes_level: "Shoes",
    // electronic_level: "Electronics",
  });

  const updateProperty = (property, value) => {
    setCategoryHeader({ ...categoryHeader, [property]: value });
  };

  const onElectrnicChange = (text) => {
    updateProperty("electronic_level", text);
  };

  const onAccessoriesChange = (text) => {
    updateProperty("accessories_level", text);
  };

  const onClothingChange = (text) => {
    updateProperty("clothing_level", text);
  };

  const onShoesChange = (text) => {
    updateProperty("shoes_level", text);
  };

  return (
    <Accordion m="xs" variant="contained">
      {/* electronics */}
      <Accordion.Item value="electronics">
        <Accordion.Control bg="gray.2" direction="row">
          <Flex justify="flex-start" align="center" direction="row" gap="md">
            <Checkbox defaultChecked />
            <Text>Electronics</Text>
          </Flex>
        </Accordion.Control>
        <Accordion.Panel>
          <Dropzone
            accept={IMAGE_MIME_TYPE}
            onDrop={setFiles}
            bg="white"
            h="10rem"
            style={{
              position: "relative",
              border: "1px solid #1b1c1b",
              borderRadius: "5px",
            }}
          >
            {previews.length === 0 ? (
              <Text
                style={{
                  position: "absolute",
                  top: "50%",
                  left: "50%",
                  transform: "translate(-50%, -50%)",
                }}
              >
                Drop images here
              </Text>
            ) : null}
            {previews}
          </Dropzone>

          <Flex direction="column">
            <Text mt="xs">Background Color</Text>
            <ColorPicker mt="xs" w="auto" size="lg" format="rgba" />
          </Flex>
          <Flex direction="column">
            <Text mt="xs">Level</Text>
            <Input
              placeholder="Input component"
              value={categoryHeader.electronic_level}
              onChange={(event) => {
                onElectrnicChange(event.target.value);
              }}
            />
          </Flex>
        </Accordion.Panel>
      </Accordion.Item>
      {/* Accessories */}
      <Accordion.Item value="accessories">
        <Accordion.Control bg="gray.2" direction="row">
          <Flex justify="flex-start" align="center" direction="row" gap="md">
            <Checkbox defaultChecked />
            <Text>Accessories</Text>
          </Flex>
        </Accordion.Control>
        <Accordion.Panel>
        <Dropzone
            accept={IMAGE_MIME_TYPE}
            onDrop={setFiles}
            bg="white"
            h="10rem"
            style={{
              position: "relative",
              border: "1px solid #1b1c1b",
              borderRadius: "5px",
            }}
          >
            {previews.length === 0 ? (
              <Text
                style={{
                  position: "absolute",
                  top: "50%",
                  left: "50%",
                  transform: "translate(-50%, -50%)",
                }}
              >
                Drop images here
              </Text>
            ) : null}
            {previews}
          </Dropzone>
          <Flex direction="column">
            <Text mt="xs">Background Color</Text>
            <ColorPicker mt="xs" w="auto" size="lg" format="rgba" />
          </Flex>
          <Flex direction="column">
            <Text mt="xs">Level</Text>
            <Input
              placeholder="Input component"
              value={categoryHeader.accessories_level}
              onChange={(event) => {
                onAccessoriesChange(event.target.value);
              }}
            />
          </Flex>
        </Accordion.Panel>
      </Accordion.Item>
      {/* Clothing */}
      <Accordion.Item value="clothing">
        <Accordion.Control bg="gray.2" direction="row">
          <Flex justify="flex-start" align="center" direction="row" gap="md">
            <Checkbox defaultChecked />
            <Text>Clothing</Text>
          </Flex>
        </Accordion.Control>
        <Accordion.Panel>
          <Dropzone
            accept={IMAGE_MIME_TYPE}
            onDrop={setFiles}
            bg="white"
            h="10rem"
            style={{
              position: "relative",
              border: "1px solid #1b1c1b",
              borderRadius: "5px",
            }}
          >
            {previews}
            <Text
              style={{
                position: "absolute",
                top: "50%",
                left: "50%",
                transform: "translate(-50%, -50%)",
              }}
            >
              Drop images here
            </Text>
          </Dropzone>
          <Flex direction="column">
            <Text mt="xs">Background Color</Text>
            <ColorPicker mt="xs" w="auto" size="lg" format="rgba" />
          </Flex>
          <Flex direction="column">
            <Text mt="xs">Level</Text>
            <Input
              placeholder="Input component"
              value={categoryHeader.clothing_level}
              onChange={(event) => {
                onClothingChange(event.target.value);
              }}
            />
          </Flex>
        </Accordion.Panel>
      </Accordion.Item>
      {/* Shoes */}
      <Accordion.Item value="shoes">
        <Accordion.Control bg="gray.2" direction="row">
          <Flex justify="flex-start" align="center" direction="row" gap="md">
            <Checkbox defaultChecked />
            <Text>Shoes</Text>
          </Flex>
        </Accordion.Control>
        <Accordion.Panel>
          <Dropzone
            accept={IMAGE_MIME_TYPE}
            onDrop={setFiles}
            bg="white"
            h="10rem"
            style={{
              position: "relative",
              border: "1px solid #1b1c1b",
              borderRadius: "5px",
            }}
          >
            {previews}
            <Text
              style={{
                position: "absolute",
                top: "50%",
                left: "50%",
                transform: "translate(-50%, -50%)",
              }}
            >
              Drop images here
            </Text>
          </Dropzone>
          <Flex direction="column">
            <Text mt="xs">Background Color</Text>
            <ColorPicker mt="xs" w="auto" size="lg" format="rgba" />
          </Flex>
          <Flex direction="column">
            <Text mt="xs">Level</Text>
            <Input
              placeholder="Input component"
              value={categoryHeader.shoes_level}
              onChange={(event) => {
                onShoesChange(event.target.value);
              }}
            />
          </Flex>
        </Accordion.Panel>
      </Accordion.Item>

      {/* Add More */}
      <Accordion.Item mt="xs" value="addmore">
        <Accordion.Control bg="gray.2" direction="row">
          <Flex justify="flex-start" align="center" direction="row" gap="md">
            <Text c={`indigo.6`}>+ Add More</Text>
          </Flex>
        </Accordion.Control>
        <Accordion.Panel>
          <Dropzone
            accept={IMAGE_MIME_TYPE}
            onDrop={setFiles}
            bg="white"
            h="10rem"
            style={{
              position: "relative",
              border: "1px solid #1b1c1b",
              borderRadius: "5px",
            }}
          >
            {previews}
            <Text
              style={{
                position: "absolute",
                top: "50%",
                left: "50%",
                transform: "translate(-50%, -50%)",
              }}
            >
              Drop images here
            </Text>
          </Dropzone>
          <Flex direction="column">
            <Text mt="xs">Background Color</Text>
            <ColorPicker mt="xs" w="auto" size="lg" format="rgba" />
          </Flex>
          <Flex direction="column">
            <Text mt="xs">Level</Text>
            <Input placeholder="Input component" />
          </Flex>
        </Accordion.Panel>
      </Accordion.Item>
    </Accordion>
  );
};

export default CustomizeCategoryList1;
