import React, { useState } from "react";
import {
  Box,
  Center,
  Stack,
  Flex,
  ActionIcon,
  Autocomplete,
  rem,
  Avatar,
} from "@mantine/core";
import {
  IconHome2,
  IconUser,
  IconMessageCircle,
  IconBookmark,
  IconAlignLeft,
  IconSearch,
  IconCircleXFilled,
  IconBell,
} from "@tabler/icons-react";

const ResponsivePhone = () => {
  const [selectedBlackIcon, setSelectedBlackIcon] = useState(null);

  const handleBlackIconClick = (index, component) => {
    console.log("Clicked icon :", component);
    setSelectedBlackIcon(index);
  };

  const blackicons = [
    { name: "IconHome2", component: <IconHome2 /> },
    { name: "IconMessageCircle", component: <IconMessageCircle /> },
    { name: "IconBookmark", component: <IconBookmark /> },
    { name: "IconUser", component: <IconUser /> },
  ];

  return (
    <Stack>
      <Center>
      <Box
  style={{
    position: "relative", // Make the main box a relative container
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    border: "2px solid #1b1c1b",
    borderRadius: "15px",
    width: "200px", // Fixed width
    height: "400px", // Fixed height
    backgroundColor: "#ffffff",
    boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.2)",
  }}
>
  {/* Top Box */}
  <Box
    bg="dark.9" // Background color for the top box
    style={{
      width: "200px", // Fixed width for the top box
      height: "30px", // Fixed height for the top box
      border: "2px solid #000", // Border for the top box
      borderRadius: "15px 15px 0px 0px", // Border radius for the top box
    }}
  >
    <Box
    bg="dark.9" // Background color for the top box
    style={{
      width: "50px", // Fixed width for the top box
      height: "5px", // Fixed height for the top box
      border: "2px solid #000", // Border for the top box
      borderRadius: "15px 15px 0px 0px", // Border radius for the top box
    }}
  >
    
  </Box>
  </Box>

  {/* Center Box (Takes the remaining space) */}
  <Box
    bg="light.2" // Background color for the centered box
    style={{
      flex: 1, // Expand to take remaining space
      width: "100%", // Full width of the main box
      border: "2px solid #000", // Border for the centered box
      // borderRadius: "5px 5px 5px 5px", // Remove border radius
      marginTop: "-2px", // Remove top border overlap
      marginBottom: "-2px", // Remove bottom border overlap
    }}
  ></Box>

  {/* Bottom Box (Positioned at the bottom) */}
  <Box
    bg="dark.9" // Background color for the bottom box
    style={{
      width: "200px", // Fixed width for the bottom box (same as top box)
      height: "30px", // Fixed height for the bottom box (same as top box)
      border: "2px solid #000", // Border for the bottom box (same as top box)
      borderRadius: "0px 0px 15px 15px", // Border radius for the bottom box (same as top box)
    }}
  ></Box>
</Box>

      </Center>
    </Stack>
  );
};

export default ResponsivePhone;
