import React, { useState } from "react";
import { Flex, Accordion, ActionIcon, Stack } from "@mantine/core";
import { IconColumns1 } from "@tabler/icons-react";

const CustomizeCategory = ({ items }) => {
  const [selectionStates, setSelectionStates] = useState(items.map(() => false));

  const toggleSelection = (index) => {
    const newSelectionStates = [...selectionStates];
    newSelectionStates[index] = !newSelectionStates[index];
    setSelectionStates(newSelectionStates);
  };

  if (!items || !Array.isArray(items)) {
    return null; // or handle the error in a different way
  }

  return (
    <>
      {items.map((item, index) => (
        <Accordion.Item key={index} value={item.controls[0]}>
          <Accordion.Control bg="#FFFFFF">{item.controls[0]}</Accordion.Control>
          <Accordion.Panel bg="#FFFFFF">
            {item.icons.map((icon, i) => (
              <ActionIcon
                key={i}
                variant={selectionStates[i] ? "filled" : "default"}
                onClick={() => toggleSelection(i)}
                color="indigo"
                w={icon.width}
                h={icon.height}
                ml={icon.marginLeft}
              >
                <Stack w="100%" gap={0}>
                  <Flex>
                    <IconColumns1 style={{ width: "100%", height: "100%" }} stroke={1} />
                    <IconColumns1 style={{ width: "100%", height: "100%" }} stroke={1} />
                    <IconColumns1 style={{ width: "100%", height: "100%" }} stroke={1} />
                  </Flex>
                </Stack>
              </ActionIcon>
            ))}
          </Accordion.Panel>
        </Accordion.Item>
      ))}
    </>
  );
};

export default CustomizeCategory;
