import React from "react";
import {
  Avatar,
  Text,
  Flex,
  Stack,
  Box,
  Grid,
  Center,
  rem,
} from "@mantine/core";
import { IconAlignLeft, IconSearch, IconBell } from "@tabler/icons-react";

const appbaritem = {
  appbar: {
    component: (
      <Box px={10} py={5} bg="gray.3">
        <Grid>
          <Grid.Col span={2}>
            <Flex gap="md" justify="flex-start" align="center">
              <IconAlignLeft />
            </Flex>
          </Grid.Col>
          <Grid.Col span={8}>
            <Flex gap="md" justify="center" align="center"></Flex>
          </Grid.Col>
          <Grid.Col span={2}>
            <Flex gap="md" justify="flex-end" align="center">
              <Box
                style={{
                  display: "flex",
                  alignItems: "center",
                  justify: "space-around",
                  gap: "xl",
                  marginRight: "4px",
                }}
              >
                <Box style={{ marginRight: "10px" }}>
                  <IconSearch size={`15px`} />
                </Box>
                <Box>
                  <Avatar
                    size="sm"
                    bg="white"
                    radius="md"
                    src="https://images.unsplash.com/photo-1535713875002-d1d0cf377fde?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=250&q=80"
                  />
                </Box>
              </Box>
            </Flex>
          </Grid.Col>
        </Grid>
      </Box>
    ),
    isSelected: false,
  },
  appbar2: {
    component: (
      <Box px={10} py={5} bg="gray.3">
        <Grid>
          <Grid.Col span={2}>
            <Flex gap="md" justify="flex-start" align="center">
              <IconAlignLeft />
            </Flex>
          </Grid.Col>
          <Grid.Col span={8}>
            <Flex gap="md" justify="center" align="center">
              <Box
                bg="gray.1"
                px={3}
                c={`gray.4`}
                style={{
                  width: "150px",
                  backgroundColor: "var(--mantine-color-gray-0)",
                  border: "1px solid var(--mantine-color-gray-4)",
                  borderRadius: "15px",
                }}
              >
                <Flex
                  gap="xs"
                  justify="flex-start"
                  align="center"
                  direction="row"
                  wrap="wrap"
                >
                  <IconSearch
                    style={{ width: rem(15), height: rem(15) }}
                    stroke={1.5}
                  />
                  <Text>Search</Text>
                </Flex>
              </Box>
            </Flex>
          </Grid.Col>
          <Grid.Col span={2}>
            <Flex gap="md" justify="flex-end" align="center">
              <Avatar size="sm" bg="white" radius="xl">
                <IconBell size={`15px`} />
              </Avatar>
            </Flex>
          </Grid.Col>
        </Grid>
      </Box>
    ),
    isSelected: false,
  },
  appbar3: {
    component: (
      <Box px={10} py={5} bg="gray.3">
        <Grid>
          <Grid.Col span={2}>
            <Flex gap="md" justify="flex-start" align="center">
              <IconAlignLeft />
            </Flex>
          </Grid.Col>
          <Grid.Col span={8}>
            <Flex gap="md" justify="center" align="center">
              <Box
                bg="gray.1"
                px={3}
                c={`gray.4`}
                style={{
                  width: "150px",
                  backgroundColor: "var(--mantine-color-gray-0)",
                  border: "1px solid var(--mantine-color-gray-4)",
                  borderRadius: "15px",
                }}
              >
                <Flex
                  gap="xs"
                  justify="flex-start"
                  align="center"
                  direction="row"
                  wrap="wrap"
                >
                  <IconSearch
                    style={{ width: rem(15), height: rem(15) }}
                    stroke={1.5}
                  />
                  <Text>Search</Text>
                </Flex>
              </Box>
            </Flex>
          </Grid.Col>
          <Grid.Col span={2}>
            <Flex gap="md" justify="flex-end" align="center">
              <Avatar
                size="sm"
                bg="white"
                radius="md"
                src="https://images.unsplash.com/photo-1535713875002-d1d0cf377fde?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=250&q=80"
              />
            </Flex>
          </Grid.Col>
        </Grid>
      </Box>
    ),
    isSelected: false,
  },
  // appbar4: (

  // ),
};

const AppBar = ({
  Item,
  setItem,
  Item2,
  setItem2,
  Item3,
  setItem3,
  appBar,
}) => {
  const handleStack1Click = () => {
    setItem(!Item);
    setItem2(false);
    setItem3(false);
    appBar({
      component: appbaritem.appbar.component,
      isSelected: true,
    });
  };

  const handleStack2Click = () => {
    setItem(false);
    setItem2(!Item2);
    setItem3(false);
    appBar({
      component: appbaritem.appbar2.component,
      isSelected: true,
    });
  };

  const handleStack3Click = () => {
    setItem(false);
    setItem2(false);
    setItem3(!Item3);
    appBar({
      component: appbaritem.appbar3.component,
      isSelected: true,
    });
  };

  return (
    <Grid>
      <Grid.Col span={6}>
        <Box
          onClick={handleStack1Click}
          style={{
            border: appbaritem.appbar.isSelected ? "2px solid blue" : "none",
          }}
        >
          {appbaritem.appbar.component}
          <Center>
            <Text size="xs">AppBar 1</Text>
          </Center>
        </Box>
      </Grid.Col>
      <Grid.Col span={6}>
        <Box
          onClick={handleStack2Click}
          style={{
            border: appbaritem.appbar2.isSelected ? "2px solid blue" : "none",
          }}
        >
          {appbaritem.appbar2.component}
          <Center>
            <Text size="xs">AppBar 2</Text>
          </Center>
        </Box>
      </Grid.Col>
      <Grid.Col span={6}>
        <Box
          onClick={handleStack3Click}
          style={{
            border: appbaritem.appbar3.isSelected ? "2px solid blue" : "none",
          }}
        >
          {appbaritem.appbar3.component}
          <Center>
            <Text size="xs">AppBar 3</Text>
          </Center>
        </Box>
      </Grid.Col>
    </Grid>
  );
};

export default AppBar;
