import React, { useState } from "react";
import { Button, SegmentedControl, Drawer } from "@mantine/core";
import {
  IconShoppingCart,
  IconLicense,
  IconMessage2,
  IconMessages,
  IconApps,
  IconSettings,
  IconLayoutSidebarLeftExpand,
  IconUsers,
  IconFileAnalytics,
  IconLayoutBottombarExpand,
  IconReceiptRefund,
} from "@tabler/icons-react";
import classes from "./NavbarSegmented.module.css";
import Appbar from "../Global/AppBar";
import Navbar from "../Global/Navbar";
import Drawer2 from "../Global/Drawer";

const tabs = {
  account: [
    { link: "", label: "Appbar", icon: IconApps },
    { link: "", label: "Navbar", icon: IconLayoutBottombarExpand },
    { link: "", label: "Drawer", icon: IconLayoutSidebarLeftExpand },
  ],
  general: [
    { link: "", label: "Orders", icon: IconShoppingCart },
    { link: "", label: "Receipts", icon: IconLicense },
    { link: "", label: "Reviews", icon: IconMessage2 },
    { link: "", label: "Messages", icon: IconMessages },
    { link: "", label: "Customers", icon: IconUsers },
    { link: "", label: "Refunds", icon: IconReceiptRefund },
    { link: "", label: "Files", icon: IconFileAnalytics },
  ],
};

const NavbarSegmented = ({
  selectAppBar,
  setSelectAppBar,
  selectAppBar2,
  setSelectAppBar2,
  onItemClick,
  selectNavBar,
  setSelectNavBar,
  selectNavBar2,
  setSelectNavBar2,
  selectNavBar3,
  setSelectNavBar3,
  onNavBarItemClick,
}) => {
  const [section, setSection] = useState("account");
  const [active, setActive] = useState("Appbar");
  const [isAppbarDrawerOpen, setIsAppbarDrawerOpen] = useState(false); // State to manage the drawer's visibility
  const [isNavbarDrawerOpen, setIsNavbarDrawerOpen] = useState(false); // State for the new Navbar drawer
  const [isDrawerDrawerOpen, setIsDrawerDrawerOpen] = useState(false); // State for the Drawer drawer
  const [isSettingsDrawerOpen, setIsSettingsDrawerOpen] = useState(false);

  const links = tabs[section].map((item) => (
    <a
      className={classes.link}
      data-active={item.label === active || undefined}
      href={item.link}
      key={item.label}
      onClick={(event) => {
        event.preventDefault();
        setActive(item.label);
        // onItemClick(item.label);

        if (item.label === "Appbar") {
          setIsAppbarDrawerOpen(true);
        } else if (item.label === "Navbar") {
          setIsNavbarDrawerOpen(true);
        } else if (item.label === "Drawer") {
          setIsDrawerDrawerOpen(true);
        }
      }}
    >
      <item.icon className={classes.linkIcon} stroke={1.5} />
      <span>{item.label}</span>
    </a>
  ));

  const closeAppbarDrawer = () => {
    setIsAppbarDrawerOpen(false);
  };

  const closeNavbarDrawer = () => {
    setIsNavbarDrawerOpen(false);
  };

  const closeDrawerDrawer = () => {
    setIsDrawerDrawerOpen(false);
  };

  // Conditionally render the footer based on the selected section
  const footer =
    section === "account" ? (
      <div className={classes.footer}>
        <a
          href="#"
          style={{
            display: "flex",
            marginRight: "10px",
            justifyContent: "space-between",
          }}
          onClick={(event) => event.preventDefault()}
        >
          <IconSettings
            className={classes.linkIcon}
            stroke={1.5}
            onClick={() => setIsDrawerDrawerOpen(true)}
          />
          <Button>Save</Button>
        </a>
      </div>
    ) : null;

  return (
    <nav className={classes.navbar}>
      <div>
        <SegmentedControl
          value={section}
          onChange={(value) => setSection(value)}
          transitionTimingFunction="ease"
          fullWidth
          data={[
            { label: "Global", value: "account" },
            { label: "Component", value: "general" },
          ]}
        />
      </div>

      <div className={classes.navbarMain}>{links}</div>

      {footer}

      <Drawer
        title="IconSettings Drawer"
        opened={isSettingsDrawerOpen}
        onClose={() => setIsSettingsDrawerOpen(false)}
        position="bottom"
        size={300}
        overlayProps={{ backgroundOpacity: 0.5, blur: 4 }}
        transitionProps={{
          transition: "rotate-right",
          duration: 500,
          timingFunction: "linear",
        }}
      >
        {/* Add the content for the IconSettings drawer here */}
        <div>
          <h3>IconSettings Drawer Content</h3>
          {/* Add your content here */}
        </div>
      </Drawer>

      {/* Drawer component with width set to 300px */}
      <Drawer
        title="App Bar Drawer"
        opened={isAppbarDrawerOpen}
        onClose={closeAppbarDrawer}
        position="left"
        size={300}
        // overlayProps={{ backgroundOpacity: 0.5, blur: 4 }}
        transitionProps={{
          transition: "rotate-right",
          duration: 500,
          timingFunction: "linear",
        }}
      >
        <Appbar
          Item={selectAppBar}
          setItem={setSelectAppBar}
          Item2={selectAppBar2}
          setItem2={setSelectAppBar2}
          appBar={onItemClick}
        />
      </Drawer>

      {/* New Navbar Drawer */}
      <Drawer
        title="Navbar Drawer"
        opened={isNavbarDrawerOpen}
        onClose={closeNavbarDrawer}
        position="left"
        size={300}
        overlayProps={{ backgroundOpacity: 0.5, blur: 4 }}
        transitionProps={{
          transition: "rotate-right",
          duration: 500,
          timingFunction: "linear",
        }}
      >
        <Navbar
          Item={selectNavBar}
          SetItem={setSelectNavBar}
          Item2={selectNavBar2}
          setItem2={setSelectNavBar2}
          Item3={selectNavBar3}
          setItem3={setSelectNavBar3}
          navBar={onNavBarItemClick}
        />
      </Drawer>

      <Drawer
        title="Drawer Drawer"
        opened={isDrawerDrawerOpen}
        onClose={closeDrawerDrawer}
        position="left"
        size={300}
        overlayProps={{ backgroundOpacity: 0.5, blur: 4 }}
      >
        <Drawer2 />
      </Drawer>
    </nav>
  );
};

export default NavbarSegmented;
