import { Box, Center, Flex, Grid, Stack, Text } from "@mantine/core";
import React from "react";

const Layout = ({ bgColor, borderRadius }) => {
    const items = ["Item 1", "Item 2", "Item 3"];
  return (
    <Box bg={bgColor} h={200} style={{ borderRadius: `${borderRadius}px` }}>
      <Stack spacing={0}>
        <Box 
          bg="lime.3" 
          // style={{ borderRadius: "10px 10px 0 0" }}
          style={{ borderRadius: `${borderRadius}px ${borderRadius}px 0 0` }}
          >
          <Center>
            <Text>Test</Text>
          </Center>
        </Box>
        <Grid ml="xs" mr="xs">
          {items.map((item) => (
            <Grid.Col span={4}>
              <Box
                bg= "blue.1"
                h={60}
                style={{ border: "1px solid #000000" }}
              >
                <Center>{item}</Center>
                
              </Box>
            </Grid.Col>
          ))}
        </Grid>
      </Stack>
    </Box>
  );
};

export default Layout;
