import React, { useState } from "react";
import {
  Box,
  Text,
  Flex,
  Accordion,
  Center,
  Badge,
  Checkbox,
  Image,
  Card,
} from "@mantine/core";

const LatestProduct = ({
  setBoxVisible,
  boxVisible,
  setBoxVisible2,
  boxVisible2,
  title,
  title2,
  setCategoryVisible,
  setCategoryVisible2,
  setCategoryVisible3,
  setCategoryVisible4,
}) => {
  const [iconCheckVisible, setIconCheckVisible] = useState(false);
  const [iconCheckVisible2, setIconCheckVisible2] = useState(false);
  const handleCategoryWithProductClick = () => {
    setBoxVisible(!boxVisible);
    setBoxVisible2(false);
    setIconCheckVisible(!iconCheckVisible);
    setIconCheckVisible2(false);
    setCategoryVisible(false);
    setCategoryVisible2(false);
    setCategoryVisible3(false);
    setCategoryVisible4(false);
  };
  const handleCategoryWithProductClick2 = () => {
    setBoxVisible2(!boxVisible2);
    setBoxVisible(false);
    setIconCheckVisible2(!iconCheckVisible2);
    setIconCheckVisible(false);
    setCategoryVisible(false);
    setCategoryVisible2(false);
    setCategoryVisible3(false);
    setCategoryVisible4(false);
  };
  return (
    <Accordion.Item value="latest -product">
      <Accordion.Control bg="indigo.2">
        <Center>
          <Text fw={700}>Latest Product / Recently Added</Text>
        </Center>
      </Accordion.Control>
      <Accordion.Panel>
        <Box
          bg="gray.2"
          style={{ cursor: "pointer" }}
          onClick={handleCategoryWithProductClick}
        >
          <Flex
            justify="space-between"
            align="center"
            direction="row"
            wrap="wrap"
          >
            <Text ml="xs">{title}</Text>
            {iconCheckVisible && (
              <Flex mr="xs" h={{ base: "auto", md: `100%` }}>
                <Checkbox defaultChecked />
              </Flex>
            )}
          </Flex>
          <Flex
            h="auto"
            pb={10}
            justify="flex-start"
            align="center"
            direction="row"
            wrap="wrap"
          >
            {products.map((product) => (
              <Card
                key={product.id}
                mt="xs"
                ml="xs"
                shadow="sm"
                padding="xs"
                radius="md"
                w={{ base: "100%", md: 90 }}
              >
                <Badge color="pink" variant="light" mb="xs">
                  On Sale
                </Badge>
                <Card.Section>
                  <Image
                    ml="xs"
                    mr="xs"
                    radius="md"
                    h={100}
                    w={70}
                    fit="contain"
                    src={product.imageSrc}
                  />
                </Card.Section>
                <Text fw={500}>{product.title}</Text>
                <Text fw={500}>{product.price}</Text>
              </Card>
            ))}
          </Flex>
        </Box>

        <Box
          bg="gray.2"
          mt="xs"
          style={{ cursor: "pointer" }}
          onClick={handleCategoryWithProductClick2}
        >
          <Flex justify="space-between" align="center" direction="row">
            <Text ml="xs">{title2}</Text>
            {iconCheckVisible2 && (
              <Flex mr="xs" h={{ base: "auto", md: `100%` }}>
                <Checkbox defaultChecked />
              </Flex>
            )}
          </Flex>
          <Flex
            h="auto"
            pb={10}
            justify="flex-start"
            align="center"
            direction="row"
            wrap="wrap"
          >
            {products2.map((product) => (
              <Card
                key={product.id}
                ml="xs"
                mt="xs"
                shadow="sm"
                padding="xs"
                radius="md"
                w={{ base: "100%", md: 145 }}
              >
                <Badge color="pink" variant="light" mb="xs">
                  On Sale
                </Badge>
                <Card.Section>
                  <Image
                    radius="md"
                    ml="xs"
                    h={100}
                    w="auto"
                    fit="contain"
                    src={product.imageSrc}
                    alt="Norway"
                  />
                </Card.Section>
                <Text fw={500}>{product.title}</Text>
                <Text fw={500}>{product.price}</Text>
              </Card>
            ))}
          </Flex>
        </Box>
      </Accordion.Panel>
    </Accordion.Item>
  );
};

export default LatestProduct;
