import React from 'react'
import { Box, Paper } from '@mantine/core';

const MobileFrame = ({ children }) => {
  return (
    <Box
      style={{
        width: '375px', // Adjust this width to match your desired mobile screen size
        height: '667px', // Adjust this height to match your desired mobile screen size
        margin: '0 auto', // Center the frame horizontally
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      <Paper padding="md" shadow="xs">
        {children}
      </Paper>
    </Box>
  );
}

export default MobileFrame
