import React, { useState } from "react";
import {
  Box,
  Text,
  Flex,
  Accordion,
  Center,
  Avatar,
  Badge,
  Checkbox,
} from "@mantine/core";

const brandData = [
  {
    name: "Electronics",
    src:
      "https://blogassets.leverageedu.com/blog/wp-content/uploads/2020/01/02232228/BSc-Electronics.png",
  },
  {
    name: "Accessories",
    src:
      "https://hnsfpau.imgix.net/5/images/detailed/240/TH-32JS610Z.01.jpg?fit=fill&bg=0FFF&w=1500&h=844&auto=format,compress",
  },
  {
    name: "Clothing",
    src:
      "https://i0.wp.com/post.healthline.com/wp-content/uploads/2020/07/Best_Baby_Clothes_1296x728.png?w=1155&h=1528",
  },
  {
    name: "Shoes",
    src:
      "https://media.gq.com/photos/63eba1b2275d2fef78a425c2/16:9/w_2560%2Cc_limit/nike-running-shoes-streakfly-invincible.jpg",
  },
];

const BrandList = () => {
  const [boxVisible, setBoxVisible] = useState([false, false, false, false]);
  const [iconCheckVisible, setIconCheckVisible] = useState([false, false, false, false]);

  const handleCategoryWithProductClick = (index) => {
    const updatedBoxVisible = boxVisible.map((_, i) => i === index);
    const updatedIconCheckVisible = iconCheckVisible.map((_, i) => i === index);
    setBoxVisible(updatedBoxVisible);
    setIconCheckVisible(updatedIconCheckVisible);
  };

  return (
    <Accordion.Item value="brand-list">
      <Accordion.Control bg="indigo.2">
        <Center>
          <Text fw={700}>Brand List</Text>
        </Center>
      </Accordion.Control>
      <Accordion.Panel>
        {brandData.map((brand, index) => (
          <Box
            key={brand.name}
            mb="xs"
            bg="#FFFFFF"
            style={{ cursor: "pointer" }}
            onClick={() => handleCategoryWithProductClick(index)}
          >
            <Flex
              h={{ base: "auto", md: 100 }}
              justify="center"
              align="center"
              direction={{ base: "column", md: "row" }}
              gap="sm"
            >
              <Flex justify="center" align="center" direction="column">
                <Avatar
                  variant="filled"
                  radius="sm"
                  size="lg"
                  color="yellow"
                  src={brand.src}
                />
                <Text size="xs">{brand.name}</Text>
              </Flex>
              {iconCheckVisible[index] && (
                <Flex justify="center" align="center" direction="row" gap="sm" h={{ base: "auto", md: `100%` }}>
                  <Checkbox defaultChecked />
                </Flex>
              )}
            </Flex>
          </Box>
        ))}
      </Accordion.Panel>
    </Accordion.Item>
  );
};

export default BrandList;
