import React, { useState } from "react";
import { useDisclosure } from "@mantine/hooks";
import { Grid } from "@mantine/core";
import ImageTest from "./ImageTest";
import TextTemplate from "./TextTemplate";
import Drawer from "./Drawer";
import Navbar from "./Navbar";
import NavbarItem from "./NavbarItem";
import ResponsivePhone from "./ResponsivePhone";
import AppBar from "../Global/AppBar";
import AccordionBar from "./AccordionBar";
import Layout from "./Layout";
import Layout2 from "./Layout2";
import Layout3 from "./Layout3";

const Index = () => {
  const [openDrawer, setOpenDrawer] = useState(false);
  const [selectedItem, setSelectedItem] = useState(null);
  const [inputValue, setInputValue] = useState(""); // New state for input value

  // Function to handle item selection
  const handleItemClick = (item) => {
    setSelectedItem(item);
  };

  // Function to handle input change
  const handleInputChange = (value) => {
    setInputValue(value);
  };

  // Function to handle input submission
  const handleInputSubmit = (input) => {
    setSelectedItem(input);
  };

  return (
    <Grid>
      <Grid.Col span={4}>
        <Layout selected={selectedItem} onItemSelect={handleItemClick} />
      </Grid.Col>
      <Grid.Col span={4}>
        <Layout2 selected={selectedItem} />
      </Grid.Col>
      <Grid.Col span={4}>
        <Layout3 inputValue={inputValue} onInputChange={handleInputChange} />
      </Grid.Col>
      <Grid.Col span={4}>
        <AccordionBar setOpenDrawer={setOpenDrawer} />
      </Grid.Col>
      <Grid.Col span={4}>
        <ResponsivePhone
          openDrawer={openDrawer}
          setOpenDrawer={setOpenDrawer}
        />
      </Grid.Col>
      <Grid.Col span={4}>
        <Navbar />
      </Grid.Col>
      <Grid.Col span={4}>
        <NavbarItem />
      </Grid.Col>
      <Grid.Col span={4}>
        <AppBar />
      </Grid.Col>
      <Grid.Col span={4}>
        <ImageTest />
      </Grid.Col>
      <Grid.Col span={4}>
        <TextTemplate />
      </Grid.Col>
      <Grid.Col span={4}>
        <Drawer />
      </Grid.Col>
    </Grid>
  );
};

export default Index;
