import React from "react";
import { Text, Box, Accordion } from "@mantine/core";
import { IconX } from "@tabler/icons-react";
import CustomizeCategoryList1 from "./CustomizeCategoryList1";
import Settings from "./Settings";

const CategoryCustomize = ({ categoryListBoxVisible }) => {

  return (
    <Box m="xs" h="570px" bg="gray.2" style={{ borderRadius: "10px", overflow }}>
      {/* Box (Header) */}
      <Box
        bg="indigo.5"
        ml="xs"
        mr="xs"
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          backgroundColor: "indigo.5",
          padding: "10px",
          borderRadius: "10px",
        }}
      >
        {/* Text */}
        <Text
          fw={900}
          c="#FFFFFF"
          style={{
            flex: "1",
            textAlign: "center",
          }}
        >
          Customize
        </Text>
        {/* IconX */}
        <IconX
          size={28}
          style={{
            borderRadius: "50%",
            background: "red",
            color: "white",
            alignSelf: "flex-end",
          }}
        />
      </Box>
      {/* Accordion */}
      <Accordion
        m="xs"
        variant="contained"
        defaultValue="category-item"
        pb={10}
      >
        {/* Accordion.Item */}
        <Accordion.Item value="category-item">
          {/* Accordion.Control */}
          <Accordion.Control bg="#FFFFFF">
            <Text>Category Item</Text>
          </Accordion.Control>
          {/* Accordion.Panel */}
          <Accordion.Panel bg="#FFFFFF">
            <CustomizeCategoryList1 />
          </Accordion.Panel>
        </Accordion.Item>
        {/* Settings */}
        <Settings />
      </Accordion>
    </Box>
  );
};

export default CategoryCustomize;
