import { Box, Center, Container, HoverCard } from "@mantine/core";
import React from "react";
import { IconAlignLeft, IconSearch, IconPhotoMinus } from "@tabler/icons-react";
const AppBar = () => {
  return (
    <div>
      <Container fluid bg="var(--mantine-color-blue-light)">
        <Center>App Bar</Center>
      </Container>
      <Box
        size="md"
        my="xs"
        bg="var(--mantine-color-blue-light)"
        radius="xl"
        className="rounded-lg p-4 transition duration-300 ease-in-out hover:bg-gray-200"
        style={{
          transitionProperty: "border-color",
          transitionDuration: "300ms",
        }}
      >
        <div
          style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "flex-end",
          }}
        >
          <div className="mr-4" style={{ alignItems: "flex-start" }}>
            <IconAlignLeft />
          </div>
          <div style={{ display: "inline-block" }}>
            <IconSearch style={{ display: "inline-block" }} />
            <IconPhotoMinus style={{ display: "inline-block" }} />
          </div>
        </div>
      </Box>
    </div>
  );
};

export default AppBar;
