                  <Stack gap={0}>
                    {/* Box Radius */}
                    <Text>Box Radius</Text>
                    <Flex
                      mih={50}
                      bg="white"
                      gap="xs"
                      justify="center"
                      align="center"
                      direction="row"
                      wrap="wrap"
                      style={{
                        border: "1px solid #1b1c1b",
                        borderRadius: "5px",
                      }}
                    >
                      <Input
                        type="number"
                        size="xs"
                        value={boxValue}
                        w={45}
                        readOnly
                        style={{ borderRadius: "20px" }}
                      />
                      <Slider
                        my="xs"
                        value={boxValue}
                        onChange={handleRadiusChange}
                        min={0}
                        max={100}
                        w={230}
                        label={null}
                      />
                      <IconPlus
                        onClick={handleRadiusIncrement}
                        style={{
                          border: "1px solid #000",
                          borderRadius: "50%",
                          cursor: "pointer",
                        }}
                      />
                    </Flex>
                    {/* padding */}
                    <Text>Padding</Text>
                    <Flex
                      mih={50}
                      bg="white"
                      gap="xs"
                      justify="center"
                      align="center"
                      direction="row"
                      wrap="wrap"
                      style={{
                        border: "1px solid #1b1c1b",
                        borderRadius: "5px",
                      }}
                    >
                      <Input
                        type="number"
                        size="xs"
                        value={paddingValue}
                        w={45}
                        readOnly
                        style={{ borderRadius: "20px" }}
                      />
                      <Slider
                        my="xs"
                        value={paddingValue}
                        onChange={handlePaddingChange}
                        min={0}
                        max={100}
                        w={230}
                        label={null}
                      />
                      <IconPlus
                        onClick={handlePaddingIncrement}
                        style={{
                          border: "1px solid #000",
                          borderRadius: "50%",
                          cursor: "pointer",
                        }}
                      />
                    </Flex>
                    {/* Margin */}
                    <Text>Margin</Text>
                    <Flex
                      mih={50}
                      bg="white"
                      gap="xs"
                      justify="center"
                      align="center"
                      direction="row"
                      wrap="wrap"
                      style={{
                        border: "1px solid #1b1c1b",
                        borderRadius: "5px",
                      }}
                    >
                      <Input
                        type="number"
                        size="xs"
                        value={marginValue}
                        w={45}
                        readOnly
                        style={{ borderRadius: "20px" }}
                      />
                      <Slider
                        my="xs"
                        value={marginValue}
                        onChange={handleMarginChange}
                        min={0}
                        max={100}
                        w={230}
                        label={null}
                      />
                      <IconPlus
                        onClick={handleMarginIncrement}
                        style={{
                          border: "1px solid #000",
                          borderRadius: "50%",
                          cursor: "pointer",
                        }}
                      />
                    </Flex>
                  </Stack>
                </Accordion.Panel>
              </Accordion.Item>
            </Accordion>
          </Box>
        )}
        {/* Category With Product Customize Box */}
        {categoryWithProductVisible && (
          <Box
            m="xs"
            h={`fit-content`}
            bg="gray.2"
            mb="xs"
            style={{ borderRadius: "10px" }}
          >
            <Box
              bg="indigo.5"
              ml="xs"
              mr="xs"
              style={{
                display: "flex",
                alignItems: "center",
                justifyContent: "space-between",
                backgroundColor: "indigo.5",
                padding: "10px",
                borderRadius: "10px",
              }}
            >
              <Text
                fw={900}
                c={`#FFFFFF`}
                style={{
                  flex: "1",
                  textAlign: "center",
                }}
              >
                Customize
              </Text>
              <IconX
                size={28}
                style={{
                  borderRadius: "50%",
                  background: "red",
                  color: "white",
                  alignSelf: "flex-end",
                }}
              />
            </Box>
            {/* Header Input */}
            <Flex
              ml="xs"
              mr="xs"
              mt="xs"
              mih={50}
              bg="#FFFFFF"
              gap="xs"
              justify="center"
              align="flex-start"
              direction="column"
              wrap="wrap"
              style={{ borderRadius: "10px" }}
            >
              <Text ml="xs">Header</Text>
              <Input
                size="xs"
                ml="xs"
                mr="xs"
                mb="xs"
                w={`95%`}
                placeholder="Input component"
                value={categoryWithProductHeaderValue}
                onChange={handleHeaderChange}
              />
            </Flex>
            {/* Type */}
            <Accordion m="xs" pb={10} variant="contained">
              <Accordion.Item value="type">
                <Accordion.Control bg="#FFFFFF">Type</Accordion.Control>
                <Accordion.Panel bg="#FFFFFF">
                  <ActionIcon variant="filled" color="indigo" w={150} h={50}>
                    <IconColumns1
                      style={{ width: "100%", height: "100%" }}
                      stroke={1}
                    />
                    <IconColumns1
                      style={{ width: "100%", height: "100%" }}
                      stroke={1}
                    />
                    <IconColumns1
                      style={{ width: "100%", height: "100%" }}
                      stroke={1}
                    />
                  </ActionIcon>

                  <ActionIcon
                    variant="default"
                    color="indigo"
                    w={100}
                    h={50}
                    ml="xs"
                  >
                    <IconColumns1
                      style={{ width: "100%", height: "100%" }}
                      stroke={1}
                    />
                    <IconColumns1
                      style={{ width: "100%", height: "100%" }}
                      stroke={1}
                    />
                  </ActionIcon>

                  <ActionIcon
                    variant="default"
                    color="indigo"
                    w={50}
                    h={50}
                    ml="xs"
                  >
                    <IconColumns1
                      style={{ width: "100%", height: "100%" }}
                      stroke={1}
                    />
                  </ActionIcon>

                  <ActionIcon variant="default" color="indigo" w={150} h={100}>
                    <Stack w={`100%`} gap={0}>
                      <Flex>
                        <IconColumns1
                          style={{ width: "100%", height: "100%" }}
                          stroke={1}
                        />
                        <IconColumns1
                          style={{ width: "100%", height: "100%" }}
                          stroke={1}
                        />
                        <IconColumns1
                          style={{ width: "100%", height: "100%" }}
                          stroke={1}
                        />
                      </Flex>
                      <Flex>
                        <IconColumns1
                          style={{ width: "100%", height: "100%" }}
                          stroke={1}
                        />
                        <IconColumns1
                          style={{ width: "100%", height: "100%" }}
                          stroke={1}
                        />
                        <IconColumns1
                          style={{ width: "100%", height: "100%" }}
                          stroke={1}
                        />
                      </Flex>
                    </Stack>
                  </ActionIcon>

                  <ActionIcon
                    variant="default"
                    color="indigo"
                    w={100}
                    h={100}
                    ml="xs"
                  >
                    <Stack w={`100%`} gap={0}>
                      <Flex>
                        <IconColumns1
                          style={{ width: "100%", height: "100%" }}
                          stroke={1}
                        />
                        <IconColumns1
                          style={{ width: "100%", height: "100%" }}
                          stroke={1}
                        />
                      </Flex>
                      <Flex>
                        <IconColumns1
                          style={{ width: "100%", height: "100%" }}
                          stroke={1}
                        />
                        <IconColumns1
                          style={{ width: "100%", height: "100%" }}
                          stroke={1}
                        />
                      </Flex>
                    </Stack>
                  </ActionIcon>

                  <ActionIcon
                    variant="default"
                    color="indigo"
                    w={50}
                    h={100}
                    ml="xs"
                  >
                    <Stack w={`100%`} gap={0}>
                      <Flex>
                        <IconColumns1
                          style={{ width: "100%", height: "100%" }}
                          stroke={1}
                        />
                      </Flex>
                      <Flex>
                        <IconColumns1
                          style={{ width: "100%", height: "100%" }}
                          stroke={1}
                        />
                      </Flex>
                    </Stack>
                  </ActionIcon>
                </Accordion.Panel>
              </Accordion.Item>
              {/* Cart Option */}
              <Accordion.Item value="cart-options">
                <Accordion.Control bg="#FFFFFF">Cart Options</Accordion.Control>
                <Accordion.Panel bg="#FFFFFF">
                  <Flex direction="column">
                    <Text>Background Color</Text>
                    <ColorPicker w="auto" size="lg" format="rgba" />
                  </Flex>
                  <Stack gap={0}>
                    {/* Box Radius */}
                    <Text>Box Radius</Text>
                    <Flex
                      mih={50}
                      bg="white"
                      gap="md"
                      justify="center"
                      align="center"
                      direction="row"
                      wrap="wrap"
                      style={{
                        border: "1px solid #1b1c1b",
                        borderRadius: "5px",
                      }}
                    >
                      <Input
                        type="number"
                        value={categoryWithProductRadiusValue}
                        w={45}
                        size="xs"
                        readOnly
                        style={{ borderRadius: "20px" }}
                      />
                      <Slider
                        my="xs"
                        value={categoryWithProductRadiusValue}
                        onChange={handleCategoryWithProductRadiusChange}
                        min={0}
                        max={100}
                        w={200}
                        label={null}
                      />
                      <IconPlus
                        onClick={handleCategoryWithProductRadiusIncrement}
                        style={{
                          border: "1px solid #000",
                          borderRadius: "50%",
                          cursor: "pointer",
                        }}
                        />
                    </Flex>
                  </Stack>
                  <Stack mt="xs" gap={5}>
                    {/* Product Name */}
                    <Stack>
                      <Flex
                        justify="space-between"
                        align="center"
                        direction="row"
                        wrap="wrap"
                      >
                        <Text>Product Name</Text>
                        <Checkbox
                          onClick={handleProductNameEdiClick}
                          checked={showProductNameEdit}
                        />
                      </Flex>
                      {showProductNameEdit && (
                        <Stack
                          gap={0}
                          style={{
                            border: "1px solid #000",
                            borderRadius: "10px",
                          }}
                        >
                          <Flex ml="xs" mr="xs" direction="column">
                            <Text>Text Color</Text>
                            <ColorPicker w="auto" size="lg" format="rgba" />
                          </Flex>
                          <Stack ml="xs" mr="xs" gap={0}>
                            {/* Box Radius */}
                            <Text>Text Size</Text>
                            <Flex
                              mih={50}
                              bg="white"
                              gap="md"
                              justify="center"
                              align="center"
                              direction="row"
                              wrap="wrap"
                            >
                              <Input
                                type="number"
                                size="xs"
                                value={productNameTextSizeValue}
                                w={45}
                                readOnly
                                style={{ borderRadius: "20px" }}
                              />
                              <Slider
                                value={productNameTextSizeValue}
                                onChange={handleProductNameTextSizeChange}
                                min={0}
                                max={20}
                                w={190}
                                label={null}
                              />
                              <IconPlus
                                onClick={handleProductNameTextSizeIncrement}
                                style={{
                                  border: "1px solid #000",
                                  borderRadius: "50%",
                                  cursor: "pointer",
                                }}
                              />
                            </Flex>
                          </Stack>
                          <Flex ml="xs" mr="xs" direction="column">
                            <Text>Text Font</Text>
                            <Flex
                              bg="#FFFFFF"
                              h={50}
                              style={{
                                border: "1px solid #000",
                                borderRadius: "10px",
                              }}
                            ></Flex>
                          </Flex>
                          <Stack gap={0}>
                            <Text ml="xs">Text Style</Text>
                            <Flex
                              mih={50}
                              bg="white"
                              ml="xs"
                              mr="xs"
                              mb="xs"
                              gap="md"
                              justify="space-around"
                              align="center"
                              direction="row"
                              wrap="wrap"
                              style={{
                                border: "1px solid #1b1c1b",
                                borderRadius: "5px",
                              }}
                            >
                              {icons.map((icon, index) => (
                                <ActionIcon
                                  key={index}
                                  variant={
                                    selectedIcon === index
                                      ? "outline"
                                      : "default"
                                  }
                                  aria-label={`Icon ${icon.component}`}
                                  onClick={() => handleIconClick(index, icon)}
                                >
                                  {icon.component}
                                </ActionIcon>
                              ))}
                            </Flex>
                          </Stack>
                        </Stack>
                      )}
                    </Stack>
                    {/* Product Rate */}
                    <Stack>
                      <Flex
                        justify="space-between"
                        align="center"
                        direction="row"
                        wrap="wrap"
                      >
                        <Text>Product Rate</Text>
                        <Checkbox
                          onClick={handleProductRateEdiClick}
                          checked={showProductRateEdit}
                        />
                      </Flex>
                      {showProductRateEdit && (
                        <Stack
                          gap={0}
                          style={{
                            border: "1px solid #000",
                            borderRadius: "10px",
                          }}
                        >
                          <Flex ml="xs" mr="xs" direction="column">
                            <Text>Text Color</Text>
                            <ColorPicker
                              mt="xs"
                              w="auto"
                              size="lg"
                              format="rgba"
                            />
                          </Flex>
                          <Stack mt="xs" ml="xs" mr="xs">
                            {/* Box Radius */}
                            <Text>Text Size</Text>
                            <Flex
                              mih={50}
                              bg="white"
                              gap="md"
                              justify="center"
                              align="center"
                              direction="row"
                              wrap="wrap"
                            >
                              <Input
                                type="number"
                                value={productNameTextSizeValue}
                                w={50}
                                readOnly
                                style={{ borderRadius: "20px" }}
                              />
                              <Slider
                                my="xs"
                                value={productNameTextSizeValue}
                                onChange={handleProductNameTextSizeChange}
                                min={0}
                                max={20}
                                w={170}
                                label={null}
                              />
                              <Button
                                onClick={handleProductNameTextSizeIncrement}
                              >
                                +
                              </Button>
                            </Flex>
                          </Stack>
                          <Flex ml="xs" mr="xs" direction="column">
                            <Text>Text Font</Text>
                            <Flex
                              bg="#FFFFFF"
                              h={50}
                              style={{
                                border: "1px solid #000",
                                borderRadius: "10px",
                              }}
                            ></Flex>
                          </Flex>
                          <Stack>
                            <Text ml="xs">Text Style</Text>
                            <Flex
                              mih={50}
                              bg="white"
                              ml="xs"
                              mr="xs"
                              mb="xs"
                              gap="md"
                              justify="space-around"
                              align="center"
                              direction="row"
                              wrap="wrap"
                              style={{
                                border: "1px solid #1b1c1b",
                                borderRadius: "5px",
                              }}
                            >
                              {icons.map((icon, index) => (
                                <ActionIcon
                                  key={index}
                                  variant={
                                    selectedIcon === index
                                      ? "outline"
                                      : "default"
                                  }
                                  aria-label={`Icon ${icon.component}`}
                                  onClick={() => handleIconClick(index, icon)}
                                >
                                  {icon.component}
                                </ActionIcon>
                              ))}
                            </Flex>
                          </Stack>
                        </Stack>
                      )}
                    </Stack>
                    {/* Category Name */}
                    <Stack>
                      <Flex
                        justify="space-between"
                        align="center"
                        direction="row"
                        wrap="wrap"
                      >
                        <Text>Category Name</Text>
                        <Checkbox
                          onClick={handleCategoryNameEdiClick}
                          checked={showCategoryNameEdit}
                        />
                      </Flex>
                      {showCategoryNameEdit && (
                        <Stack
                          gap={0}
                          style={{
                            border: "1px solid #000",
                            borderRadius: "10px",
                          }}
                        >
                          <Flex ml="xs" mr="xs" direction="column">
                            <Text>Text Color</Text>
                            <ColorPicker w="auto" size="lg" format="rgba" />
                          </Flex>
                          <Stack ml="xs" mr="xs" gap={0}>
                            {/* Box Radius */}
                            <Text>Text Size</Text>
                            <Flex
                              mih={50}
                              bg="white"
                              gap="md"
                              justify="center"
                              align="center"
                              direction="row"
                              wrap="wrap"
                            >
                              <Input
                                type="number"
                                value={productNameTextSizeValue}
                                w={50}
                                readOnly
                                style={{ borderRadius: "20px" }}
                              />
                              <Slider
                                my="xs"
                                value={productNameTextSizeValue}
                                onChange={handleProductNameTextSizeChange}
                                min={0}
                                max={20}
                                w={170}
                                label={null}
                              />
                              <Button
                                onClick={handleProductNameTextSizeIncrement}
                              >
                                +
                              </Button>
                            </Flex>
                          </Stack>
                          <Flex ml="xs" mr="xs" direction="column">
                            <Text>Text Font</Text>
                            <Flex
                              bg="#FFFFFF"
                              h={50}
                              style={{
                                border: "1px solid #000",
                                borderRadius: "10px",
                              }}
                            ></Flex>
                          </Flex>
                          <Stack gap={0}>
                            <Text ml="xs">Text Style</Text>
                            <Flex
                              mih={50}
                              bg="white"
                              ml="xs"
                              mr="xs"
                              mb="xs"
                              gap="md"
                              justify="space-around"
                              align="center"
                              direction="row"
                              wrap="wrap"
                              style={{
                                border: "1px solid #1b1c1b",
                                borderRadius: "5px",
                              }}
                            >
                              {icons.map((icon, index) => (
                                <ActionIcon
                                  key={index}
                                  variant={
                                    selectedIcon === index
                                      ? "outline"
                                      : "default"
                                  }
                                  aria-label={`Icon ${icon.component}`}
                                  onClick={() => handleIconClick(index, icon)}
                                >
                                  {icon.component}
                                </ActionIcon>
                              ))}
                            </Flex>
                          </Stack>
                        </Stack>
                      )}
                    </Stack>
                    {/* Add Cart Icon */}
                    <Flex
                      justify="space-between"
                      align="center"
                      direction="row"
                      wrap="wrap"
                    >
                      <Text>Add Cart Icon</Text>
                      <Checkbox defaultChecked />
                    </Flex>
                    {/* Offer */}
                    <Flex
                      justify="space-between"
                      align="center"
                      direction="row"
                      wrap="wrap"
                    >
                      <Text>Offer</Text>
                      <Checkbox defaultChecked />
                    </Flex>
                    {/* Favorite Icon */}
                    <Flex
                      justify="space-between"
                      align="center"
                      direction="row"
                      wrap="wrap"
                    >
                      <Text>Favorite Icon</Text>
                      <Checkbox defaultChecked />
                    </Flex>
                    {/* Ratting */}
                    <Flex
                      justify="space-between"
                      align="center"
                      direction="row"
                      wrap="wrap"
                    >
                      <Text>Ratting</Text>
                      <Checkbox defaultChecked />
                    </Flex>
                  </Stack>
                </Accordion.Panel>
              </Accordion.Item>
            </Accordion>
          </Box>
        )}
      </Grid.Col>
    </Grid>
  );
};

export default Home;
