import { Menu, Group, Center, Burger, Container } from "@mantine/core";
import { useDisclosure } from "@mantine/hooks";
import { IconChevronDown } from "@tabler/icons-react";
import classes from "./css/HeaderMenu.module.css";

import {
  IconHome2,
  IconWorld,
  IconCategory,
  IconDatabase,
  IconGardenCart,
  IconTruckDelivery,
  IconUser,
  IconSettings,
} from "@tabler/icons-react";

const links = [
  {
    link: "#1",
    icon: <IconHome2 />,
    label: "Home",
    links: [
      { link: "/docs", label: "Documentation", },
      { link: "/resources", label: "Resources", icon: <IconCategory /> },
      { link: "/community", label: "Community", icon: <IconUser /> },
      { link: "/blog", label: "Blog", icon: <IconGardenCart /> },
    ],
  },
  {
    link: "#2",
    icon: <IconWorld />,
    label: "Global",
    links: [
      { link: "/docs", label: "App Bar", icon: <IconSettings /> },
      { link: "/resources", label: "Nav Bar", icon: <IconCategory /> },
      { link: "/community", label: "Drawer", icon: <IconUser /> },
    ],
  },
  // ... (other links)
];

export default function HeaderMenu() {
  const [opened, { toggle }] = useDisclosure(false);

  const items = links.map((link) => {
    const menuItems = link.links?.map((item) => (
      <Menu.Item key={item.link}>
        {item.icon} {item.label}
      </Menu.Item>
    ));

    if (menuItems) {
      return (
        <Menu
          key={link.label}
          trigger="hover"
          transitionProps={{ exitDuration: 0 }}
          withinPortal
        >
          <Menu.Target>
            <a
              href={link.link}
              className={classes.link}
              onClick={(event) => event.preventDefault()}
            >
              <Center>
                {link.icon} {/* Add the link's icon */}
                <span className={classes.linkLabel}>{link.label}</span>
                <IconChevronDown size="0.9rem" stroke={1.5} />
              </Center>
            </a>
          </Menu.Target>
          <Menu.Dropdown>{menuItems}</Menu.Dropdown>
        </Menu>
      );
    }

    return (
      <a
        key={link.label}
        href={link.link}
        className={classes.link}
        onClick={(event) => event.preventDefault()}
      >
        {link.icon} {/* Add the link's icon */}
        {link.label}
      </a>
    );
  });

  return (
    <header className={classes.header}>
      <Container size="md">
        <div className={classes.inner}>
          {/* <MantineLogo size={28} /> */}
          <Group gap={5} visibleFrom="sm">
            {items}
          </Group>
          <Burger opened={opened} onClick={toggle} size="sm" hiddenFrom="sm" />
        </div>
      </Container>
    </header>
  );
}
