import React, { useState } from "react";
import {
  Flex,
  Stack,
  Text,
  Container,
  Box,
  Accordion,
  Radio,
  Group,
  Center,
  ColorInput,
  Slider,
  Input,
  Button,
  ColorPicker,
} from "@mantine/core";
import {
  IconCircleXFilled,
  IconAlignLeft,
  IconAlignCenter,
  IconAlignJustified,
  IconAlignRight,
  IconAdjustmentsHorizontal,
  IconBaselineDensityMedium,
  IconBaselineDensitySmall,
} from "@tabler/icons-react";



const Drawer = () => {
  const [value, setValue] = useState(1);

  const handleChange = (newValue) => {
    setValue(newValue);
  };

  const handleIncrement = () => {
    const newValue = value + 1;
    if (newValue <= 100) {
      setValue(newValue);
    }
  };

  return (
    <Box bg="gray.3">
      <Container
        fluid
        bg="indigo.6"
        style={{
          borderRadius: "5px",
          display: "flex",
          height: "40px",
          alignItems: "center",
          justifyContent: "space-between",
        }}
      >
        <Text
          color="white"
          style={{ display: "inline-block", justifyContent: "flex-center" }}
        >
          Drawer Icon
        </Text>
        <div style={{ display: "flex", justifyContent: "flex-end" }}>
          <IconCircleXFilled />
        </div>
      </Container>

      <Stack m="xs">
        <Text ml="xs">Change Icon</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          <Radio.Group m="xs">
            <Group>
              <Radio
                value="medium"
                label={
                  <IconBaselineDensityMedium
                    style={{ border: "1px solid #ccc" }}
                  />
                }
              />
              <Radio
                value="small"
                label={
                  <IconBaselineDensitySmall
                    style={{ border: "1px solid #ccc" }}
                  />
                }
              />
              <Radio
                value="center"
                label={<IconAlignCenter style={{ border: "1px solid #ccc" }} />}
              />
              <Radio
                value="justifi"
                label={
                  <IconAlignJustified style={{ border: "1px solid #ccc" }} />
                }
              />
              <Radio
                value="right"
                label={<IconAlignRight style={{ border: "1px solid #ccc" }} />}
              />
            </Group>
          </Radio.Group>
        </Flex>
      </Stack>

      <Stack m="xs">
        <Text ml="xs">Background</Text>
        {/* <ColorInput
          ml="xs"
          mr="xs"
          placeholder="Choose Background Colors"
          style={{ display: "inline-block" }}
        /> */}
        <ColorPicker ml="xs" mr="xs" w="22rem" size="lg" format="rgba" />
      </Stack>

      <Stack m="xs">
        <Text ml="xs">Icon Color</Text>
        {/* <ColorInput
          ml="xs"
          mr="xs"
          placeholder="Choose Icon colors"
          style={{ display: "inline-block" }}
        /> */}
        <ColorPicker ml="xs" mr="xs" w="22rem" size="lg" format="rgba" />
      </Stack>

      <Stack m="xs">
        <Text ml="xs">Size</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
