import React, { useState } from "react";
import {
  Flex,
  Stack,
  Text,
  ActionIcon,
  Radio,
  Group,
  Slider,
  ColorPicker,
} from "@mantine/core";
import { Dropzone, IMAGE_MIME_TYPE } from "@mantine/dropzone";
import {
  IconCircleXFilled,
  IconAlignCenter,
  IconAlignJustified,
  IconAlignRight,
} from "@tabler/icons-react";
const ImageTest = () => {
  const [value, setValue] = useState(1);
  const [selectedIcon, setSelectedIcon] = useState(null);

  const handleChange = (newValue) => {
    setValue(newValue);
  };

  const handleIconClick = (index, component) => {
    console.log("Clicked icon :", component);
    setSelectedIcon(index);
  };

  const icons = [
    { name: "IconAlignCenter", component: <IconAlignCenter /> },
    { name: "IconAlignJustified", component: <IconAlignJustified /> },
    { name: "IconAlignRight", component: <IconAlignRight /> },
  ];
  return (
    <Stack bg="gray.3">
      <Flex
        bg="indigo.6"
        style={{
          borderRadius: "5px",
          display: "flex",
          height: "40px",
          alignItems: "center",
          justifyContent: "space-between",
        }}
        justify="space-between" 
        fullWidth rightSection={icon} variant="default" mt="md"
      >
        <Text
          ml="xs"
          color="white"
          style={{ display: "inline-block", justifyContent: "flex-center" }}
        >
          Image
        </Text>
        <div
          style={{
            display: "flex",
            justifyContent: "flex-end",
            marginRight: "1rem",
          }}
        >
          <IconCircleXFilled />
        </div>
      </Flex>

      <Stack>
        <Text style={{ marginLeft: "1rem" }}>Image</Text>
        <Dropzone
          bg="white"
          h="5rem"
          ml="xs"
          mr="xs"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          <Text mt="1.5rem" ta="center">
            Select Image
          </Text>
        </Dropzone>
      </Stack>

      <Stack>
        <Text style={{ marginLeft: "1rem" }}>Hight</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          <Slider
            value={value}
            onChange={handleChange}
            min={0}
            max={100}
            labelAlwaysOn
            style={{ width: "300px" }}
          />
        </Flex>
      </Stack>

      <Stack>
        <Text style={{ marginLeft: "1rem" }}>Width</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          <Slider
            value={value}
            onChange={handleChange}
            min={0}
            max={100}
            labelAlwaysOn
            style={{ width: "300px" }}
          />
        </Flex>
      </Stack>

      <Stack>
        <Text style={{ marginLeft: "1rem" }}>Alignment</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          {icons.map((icon, index) => (
            <ActionIcon
              key={index}
              variant={selectedIcon === index ? "outline" : "default"}
              aria-label={`Icon ${icon.component}`}
              onClick={() => handleIconClick(index, icon)}
            >
              {icon.component}
            </ActionIcon>
          ))}
        </Flex>
      </Stack>

      <Stack>
        <Text style={{ marginLeft: "1rem" }}>Boxfit</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          <Radio.Group>
            <Group>
              <Radio value="cover" label="Cover" />
              <Radio value="contain" label="Contain" />
              <Radio value="fill" label="Fill" />
            </Group>
          </Radio.Group>
        </Flex>
      </Stack>

      <Stack>
        <Text style={{ marginLeft: "1rem" }}>Background Color</Text>
        <ColorPicker ml="xs" mr="xs" w="auto" size="lg" format="rgba" />
      </Stack>

      <Stack>
        <Text style={{ marginLeft: "1rem" }}>Back Button Radious</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          <Flex
            mih={50}
            gap="md"
            ml="xs"
            justify="center"
            align="flex-start"
            direction="column"
            wrap="wrap"
          >
            <Text mt="xs">Left</Text>

            <Slider
              labelAlwaysOn
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
            <Text>Right</Text>

            <Slider
              labelAlwaysOn
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
            <Text>Top</Text>

            <Slider
              labelAlwaysOn
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
            <Text>Bottom</Text>

            <Slider
              labelAlwaysOn
              mb="xs"
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
          </Flex>
        </Flex>
      </Stack>

      <Stack>
        <Text style={{ marginLeft: "1rem" }}>Padding</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          <Flex
            mih={50}
            gap="md"
            ml="xs"
            justify="center"
            align="flex-start"
            direction="column"
            wrap="wrap"
          >
            <Text mt="xs">Left</Text>

            <Slider
              labelAlwaysOn
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
            <Text>Right</Text>

            <Slider
              labelAlwaysOn
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
            <Text>Top</Text>

            <Slider
              labelAlwaysOn
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
            <Text>Bottom</Text>

            <Slider
              labelAlwaysOn
              mb="xs"
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
          </Flex>
        </Flex>
      </Stack>

      <Stack mb="xs">
        <Text style={{ marginLeft: "1rem" }}>Margin</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          <Flex
            mih={50}
            gap="md"
            ml="xs"
            justify="center"
            align="flex-start"
            direction="column"
            wrap="wrap"
          >
            <Text mt="xs">Left</Text>

            <Slider
              labelAlwaysOn
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
            <Text>Right</Text>

            <Slider
              labelAlwaysOn
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
            <Text>Top</Text>

            <Slider
              labelAlwaysOn
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
            <Text>Bottom</Text>

            <Slider
              labelAlwaysOn
              mb="xs"
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
          </Flex>
        </Flex>
      </Stack>
    </Stack>
  );
};

export default ImageTest;
