import React, { useState } from "react";
import {
  Stack,
  Text,
  ColorPicker,
  Flex,
  Slider,
  Input,
  Accordion,
  rem,
} from "@mantine/core";
import { IconPhoto, IconPrinter, IconCameraSelfie } from "@tabler/icons-react";

const Layout3 = ({
  onColorChange,
  boxBorderRadius,
  onBorderRadiusChange,
  boxShadow,
  onBoxShadowChange,
  text,
  onTextChange,
  onTextBgColorChange,
  textBorderRadius,
  onTextBorderRadiusChange,
}) => {
  return (
    <Stack bg="gray.3">
      <Accordion variant="contained">
        <Accordion.Item value="photos">
          <Accordion.Control>Box</Accordion.Control>
          <Accordion.Panel>

          </Accordion.Panel>
        </Accordion.Item>

        <Accordion.Item value="print">
          <Accordion.Control
            icon={
              <IconPrinter
                style={{
                  color: "var(--mantine-color-blue-filled",
                  width: rem(20),
                  height: rem(20),
                }}
              />
            }
          >
            Print photos
          </Accordion.Control>
          <Accordion.Panel>Content</Accordion.Panel>
        </Accordion.Item>

        <Accordion.Item value="camera">
          <Accordion.Control
            icon={
              <IconCameraSelfie
                style={{
                  color: "var(--mantine-color-teal-filled)",
                  width: rem(20),
                  height: rem(20),
                }}
              />
            }
          >
            Camera settings
          </Accordion.Control>
          <Accordion.Panel>Content</Accordion.Panel>
        </Accordion.Item>
      </Accordion>
      
      <Stack>
        <Text style={{ marginLeft: "1rem" }}>Heading Text</Text>
        <Input
          type="text"
          value={text}
          onChange={(event) => {
            onTextChange(event.target.value);
          }}
          ml="xs"
          mr="xs"
          mb="xs"
        />
      </Stack>
      <Stack>
        <Text style={{ marginLeft: "1rem" }}>Heading Background Color</Text>
        <ColorPicker
          ml="xs"
          mr="xs"
          mb="xs"
          w="auto"
          size="lg"
          format="rgba"
          onChange={(color) => {
            onTextBgColorChange(color);
          }}
        />
      </Stack>
      <Stack>
        <Text style={{ marginLeft: "1rem" }}>Heading Border Radius</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          <Slider
            value={textBorderRadius}
            onChange={(newValue) => {
              onTextBorderRadiusChange(newValue);
            }}
            min={0}
            max={50}
            labelAlwaysOn
            style={{ width: "300px" }}
          />
        </Flex>
      </Stack>
    </Stack>
  );
};

export default Layout3;
