import React, { useState } from "react";
import {
  Box,
  Text,
  Flex,
  Accordion,
  Center,
  Badge,
  Checkbox,
  Image,
  Card,
} from "@mantine/core";

const BrandWithProduct = ({ sections }) => {
  const [activeIndex, setActiveIndex] = useState(null);

  const handleCategoryWithProductClick = (index) => {
    setActiveIndex(index === activeIndex ? null : index);
  };

  return (
    <Accordion.Item value="brand-with-product">
      <Accordion.Control bg="indigo.2">
        <Center>
          <Text fw={700}>Brand With Product</Text>
        </Center>
      </Accordion.Control>
      <Accordion.Panel>
        {sections.map((section, index) => (
          <Box
            key={index}
            bg="gray.2"
            style={{ cursor: "pointer" }}
            onClick={() => handleCategoryWithProductClick(index)}
          >
            <Flex justify="space-between" align="center" direction="row">
              <Text ml="xs">{section.title}</Text>
              {activeIndex === index && (
                <Flex mr="xs" h={{ base: "auto", md: `100%` }}>
                  <Checkbox defaultChecked />
                </Flex>
              )}
            </Flex>
            <Flex
              h={section.title === sections[0].title ? 220 : 440}
              gap="2px"
              justify="flex-start"
              align="center"
              direction="row"
              wrap="wrap"
            >
              {section.cards.map((card, cardIndex) => (
                <Card key={cardIndex} ml="xs" shadow="sm" padding="xs" radius="md" w={card.width}>
                  <Badge color="pink" variant="light" mb="xs">
                    {card.badgeText}
                  </Badge>
                  <Card.Section>
                    <Image
                      ml="xs"
                      mr="xs"
                      radius="md"
                      h={100}
                      w="auto"
                      fit="contain"
                      src={card.imageSrc}
                      alt={card.alt}
                    />
                  </Card.Section>
                  <Text fw={500}>{card.brand}</Text>
                  <Text fw={500}>{card.price}</Text>
                </Card>
              ))}
            </Flex>
          </Box>
        ))}
      </Accordion.Panel>
    </Accordion.Item>
  );
};

export default BrandWithProduct;
