// import { Grid, Text } from '@mantine/core'
import React, { useState } from "react";
import {
  Flex,
  Stack,
  Text,
  ActionIcon,
  Slider,
  ColorPicker,
  Avatar,
  Group,
} from "@mantine/core";
import {
  IconCircleXFilled,
  IconHome2,
  IconUser,
  IconSearch,
  IconMessageCircle,
  IconShoppingBag,
} from "@tabler/icons-react";

const Navbar = () => {
  const [selectedIcon, setSelectedIcon] = useState(null);

  const handleIconClick = (index, component) => {
    console.log("Clicked icon :", component);
    setSelectedIcon(index);
  };

  const icons = [
    { name: "IconHome2", component: <IconHome2 /> },
    { name: "IconMessageCircle", component: <IconMessageCircle /> },
    { name: "IconSearch", component: <IconSearch /> },
    { name: "IconShoppingBag", component: <IconShoppingBag /> },
    { name: "IconUser", component: <IconUser /> },
  ];
  return (
    <Stack bg="gray.3">
      <Flex
        bg="indigo.6"
        style={{
          borderRadius: "5px",
          display: "flex",
          height: "40px",
          alignItems: "center",
          justifyContent: "space-between",
        }}
      >
        <Text
          ml="xs"
          color="white"
          style={{ display: "inline-block", justifyContent: "flex-center" }}
        >
          Navbar
        </Text>
        <div
          style={{
            display: "flex",
            justifyContent: "flex-end",
            marginRight: "1rem",
          }}
        >
          <IconCircleXFilled />
        </div>
      </Flex>

      <Stack m="xs" bg="indigo.2">
        <Flex
          mih={50}
          bg="white"
          m="xs"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "25px" }}
        >
          {icons.map((icon, index) => (
            <ActionIcon
              radius="xl"
              key={index}
              variant={selectedIcon === index ? "outline" : "default"}
              aria-label={`Icon ${icon.component}`}
              onClick={() => handleIconClick(index, icon)}
            >
              {/* <Avatar>{icon.component}</Avatar> */}
              {icon.component}
            </ActionIcon>
          ))}
        </Flex>
      </Stack>

      <Flex
  minHeight={50}
  bg="dark.9"
  ml="xl"
  mr="xl"
  justify="center"
  align="center"
  direction="row"
  wrap="wrap"
  style={{
    border: "1px solid #1b1c1b",
    borderRadius: "0 0 25px 25px",
    padding: "10px", // Add padding to create space for the icon
  }}
>
  <IconHome2 style={{ color: "cyan 2" }} /> {/* Add style to make the icon visible */}
</Flex>


      <Flex
        mih={50}
        bg="white"
        ml="xl"
        mr="xl"
        mb="xs"
        justify="space-around"
        align="center"
        direction="row"
        wrap="wrap"
        style={{ border: "1px solid #1b1c1b", borderRadius: "0 0 25px 25px" }}
      >
          <IconHome2 />
          <IconUser />
          <IconSearch />
          <IconMessageCircle />
      </Flex>
    </Stack>
  );
};

export default Navbar;
