import React, { useState } from "react";
import {
  Box,
  Grid,
  Text,
  Flex,
  Accordion,
  Center,
  Avatar,
  Badge,
  Checkbox,
  Button,
  ColorPicker,
  Input,
  Stack,
  Image,
  Card,
  Slider,
  ActionIcon,
} from "@mantine/core";
import {
  IconPlus,
  IconX,
  IconColumns1,
  IconBold,
  IconUnderline,
  IconItalic,
  IconLetterN,
} from "@tabler/icons-react";

const CustomizeCategory = () => {
  const [categoryWithProductRadiusValue, setCategoryWithProductRadiusValue] =
    useState(1);

  const handleCategoryWithProductRadiusChange = (newValue) => {
    setCategoryWithProductRadiusValue(newValue);
  };

  const [showProductNameEdit, setShowProductNameEdit] = useState(false);
  const [showProductRateEdit, setShowProductRateEdit] = useState(false);
  const [showCategoryNameEdit, setShowCategoryNameEdit] = useState(false);

  const handleProductNameEdiClick = () => {
    setShowProductNameEdit(!showProductNameEdit);
    setShowProductRateEdit(false);
    setShowCategoryNameEdit(false);
  };

  const handleProductRateEdiClick = () => {
    setShowProductRateEdit(!showProductRateEdit);
    setShowProductNameEdit(false);
    setShowCategoryNameEdit(false);
  };

  const handleCategoryNameEdiClick = () => {
    setShowCategoryNameEdit(!showCategoryNameEdit);
    setShowProductRateEdit(false);
    setShowProductNameEdit(false);
  };

  // Define your configuration array
  const customizationSettings = [
    {
      title: "Product Name",
      showEdit: showProductNameEdit,
      handleEditClick: handleProductNameEdiClick,
      textColorLabel: "Text Color",
      textSizeLabel: "Text Size",
      textFontLabel: "Text Font",
    },
    {
      title: "Product Rate",
      showEdit: showProductRateEdit,
      handleEditClick: handleProductRateEdiClick,
      textColorLabel: "Text Color",
      textSizeLabel: "Text Size",
      textFontLabel: "Text Font",
    },
    {
      title: "Category Name",
      showEdit: showCategoryNameEdit,
      handleEditClick: handleCategoryNameEdiClick,
      textColorLabel: "Text Color",
      textSizeLabel: "Text Size",
      textFontLabel: "Text Font",
    },
  ];

  return (
    <>
      {/* Render the accordion items based on the configuration array */}
      {customizationSettings.map((setting, index) => (
        <Accordion.Item key={index} value={`customization-${index}`}>
          <Accordion.Control bg="#FFFFFF">{setting.title}</Accordion.Control>
          <Accordion.Panel bg="#FFFFFF">
            <Flex direction="column">
              {/* Edit Checkbox */}
              <Flex justify="space-between" align="center" direction="row" wrap="wrap">
                <Text>{setting.title}</Text>
                <Checkbox onClick={setting.handleEditClick} checked={setting.showEdit} />
              </Flex>

              {/* Text Color */}
              <ColorPicker w="auto" size="lg" format="rgba" />

              {/* Text Size */}
              {/* ... (rest of the customization settings) */}
            </Flex>
          </Accordion.Panel>
        </Accordion.Item>
      ))}

      {/* Additional settings like Add Cart Icon, Offer, Favorite Icon, Ratting */}
      {/* ... (code for additional settings) */}
    </>
  );
};

export default CustomizeCategory;
