import React, { useState } from "react";
import {
  ColorPicker,
  Grid,
  Box,
  Text,
  Center,
  Flex,
  Slider,
  Input,
} from "@mantine/core";

const Layout = () => {
  const [layout, setLayout] = useState({
    background_color: "#cdcdcd",
    box_background_color: "#FFE066",
    border_radius: 10,
    heading_text: "Hello",
    items_coloum: 3,
    category: "new",
    shadow: 2,
  });

  const updateProperty = (property, value) => {
    setLayout({ ...layout, [property]: value });
  };

  const onTextChange = (text) => {
    updateProperty("heading_text", text);
  };

  return (
    <Grid>
      <Grid.Col span={6}>
        <Box
          p={20}
          bg={layout.background_color}
          h={300}
          w={`100%`}
          style={{ borderRadius: layout.border_radius }}
        >
          <Box
            bg={layout.box_background_color}
            h={30}
            w={`100%`}
            justify="center"
            align="center"
          >
            <Text>{layout.heading_text}</Text>
          </Box>
        </Box>
      </Grid.Col>
      <Grid.Col span={6}>
        <ColorPicker
          size="lg"
          format="rgba"
          onChange={(value) => updateProperty("background_color", value)}
        />
        <ColorPicker
          mt="xs"
          size="lg"
          format="rgba"
          onChange={(value) => updateProperty("box_background_color", value)}
        />

        <Flex
          mih={50}
          w={280}
          mt="xs"
          bg="white"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          <Slider
            value={layout.border_radius}
            onChange={(value) => updateProperty("border_radius", value)}
            min={0}
            max={50}
            labelAlwaysOn
            style={{ width: "250px" }}
          />
        </Flex>
        <Text>Heading Text</Text>
        <Input
          type="text"
          value={layout.heading_text}
          onChange={(event) => {
            onTextChange(event.target.value);
          }}
          w={280}
          mt="xs"
        />
      </Grid.Col>
    </Grid>
  );
};

export default Layout;
