import React, { useState } from "react";
import {
  Box,
  Text,
  Grid,
  Center,
  Flex,
  Avatar,
  Checkbox,
  Accordion,
} from "@mantine/core";
import { IconX } from "@tabler/icons-react";
import ResponsivePhone from "../ResponsivePhone";

const Index = () => {
  const [showResponsivePhone, setShowResponsivePhone] = useState(false);

  const handleBoxClick = () => {
    setShowResponsivePhone(true);
  };

  return (
    <Grid mt="xs" pb="xs">
      <Grid.Col span={4}>
        <Box
          onClick={handleBoxClick}
          bg="indigo.5"
          ml="xs"
          mr="xs"
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
            backgroundColor: "indigo.5",
            padding: "10px",
            borderRadius: "10px",
          }}
        >
          <Text
            fw={900}
            c={`#FFFFFF`}
            style={{
              flex: "1",
              textAlign: "center",
            }}
          >
            Layouts
          </Text>
          <IconX
            size={28}
            style={{
              borderRadius: "50%",
              background: "red",
              color: "white",
              alignSelf: "flex-end",
              cursor: "pointer",
            }}
          />
        </Box>
      </Grid.Col>
      <Grid.Col span={4}>
        {/* Other content */}
      </Grid.Col>
      {showResponsivePhone && (
        <Grid.Col span={4}>
          <ResponsivePhone />
        </Grid.Col>
      )}
    </Grid>
  );
};

export default Index;
