import { Box, Center, Flex, Grid, Stack, Text } from "@mantine/core";
import React from "react";

const Layout = ({ numRows, numCols, boxHeight }) => {
    const generateBlankBoxes = () => {
        const blankBoxes = [];
    
        for (let row = 0; row < numRows; row++) {
          const rowBoxes = [];
          for (let col = 0; col < numCols; col++) {
            rowBoxes.push(
              <Grid.Col key={col} span={4}>
                <Box bg="white" h={boxHeight} style={{ border: "1px solid #000000" }}>
                  {/* This is a blank box with a height of {boxHeight}px */}
                </Box>
              </Grid.Col>
            );
          }
          blankBoxes.push(
            <Grid key={row} ml="xs" mr="xs">
              {rowBoxes}
            </Grid>
          );
        }
    
        return blankBoxes;
      };
    
  return (
    <Box bg="gray.4" h={200} style={{ borderRadius: "10px" }}>
      <Stack spacing={0}>
        <Box bg="lime.3" style={{ borderRadius: "10px 10px 0 0" }}>
          <Center>
            <Text>Test</Text>
          </Center>
        </Box>
        <Grid ml="xs" mr="xs">
          <Grid.Col span={4}>
            <Box bg="white" h={60} style={{ border: "1px solid #000000" }}>
              {/* This is a blank box with a height of 60px */}
            </Box>
          </Grid.Col>
          <Grid.Col span={4}>
            <Box bg="white" h={60} style={{ border: "1px solid #000000" }}>
              {/* Another blank box with a height of 60px */}
            </Box>
          </Grid.Col>
          <Grid.Col span={4}>
            <Box bg="white" h={60} style={{ border: "1px solid #000000" }}>
              {/* Yet another blank box with a height of 60px */}
            </Box>
          </Grid.Col>
        </Grid>
      </Stack>
    </Box>
  );
};

export default Layout;
