import React, { useState } from "react";
import { useDisclosure } from "@mantine/hooks";
import {
  Box,
  Center,
  Stack,
  Flex,
  ActionIcon,
  Autocomplete,
  Drawer,
  rem,
  Avatar,
  Text,
  Button,
  Paper,
  Menu,
  LoadingOverlay,
  Divider,
} from "@mantine/core";
import {
  IconHome2,
  IconUser,
  IconMessageCircle,
  IconBookmark,
  IconAlignLeft,
  IconSearch,
  IconCircleXFilled,
  IconBell,
  IconExternalLink,
  IconCategory2,
  IconLayoutDashboard,
  IconShoppingCart,
  IconList,
  IconLogout,
  IconRotateClockwise,
  IconCircleX,
} from "@tabler/icons-react";

const ResponsivePhone = ({
  openDrawer,
  setOpenDrawer,
  selectedCategory,
  categories,
}) => {
  const [visible] = useDisclosure(false);
  {
    /* Is true loader is visible */
  }

  const [selectedBlackIcon, setSelectedBlackIcon] = useState(null);
  const [selectedItem, setSelectedItem] = useState(null);

  const bgColor = visible ? "blue" : "gray.2";

  const handleBlackIconClick = (index, component) => {
    console.log("Clicked icon :", component);
    setSelectedBlackIcon(index);
  };

  const blackicons = [
    { name: "IconHome2", component: <IconHome2 /> },
    { name: "IconMessageCircle", component: <IconMessageCircle /> },
    { name: "IconBookmark", component: <IconBookmark /> },
    { name: "IconUser", component: <IconUser /> },
  ];

  return (
    <Stack>
      <Center>
        <Box
          className="responsive-phone"
          pos="relative"
          style={{
            position: "relative",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            maxWidth: "250px",
            width: "250px",
            height: "500px",
            backgroundColor: "var(--mantine-color-gray-0)",
            border: "2px solid var(--mantine-color-dark-9)",
            borderRadius: "18px",
            boxShadow: "5px 5px 10px rgba(0, 0, 0, 0.5)",
          }}
        >
          {/* This is the first Box */}
          <Box
            bg="dark.9"
            style={{
              width: "100%",
              height: "30px",
              borderRadius: "14px 14px 0px 0px",
            }}
          >
            <Center mt="xs">
              <Box
                bg="white"
                style={{
                  width: "50px",
                  height: "5px",
                  borderRadius: "20px",
                }}
              />
            </Center>
          </Box>
          {/* This is Display Box */}
          <Box
            bg={bgColor}
            style={{
              flex: 1,
              width: "100%",
              position: "relative",
              border: "2px solid var(--mantine-color-dark-9)",
              display: "flex",
              flexDirection: "column",
            }}
          >
            <Stack bg="gray.3">
              <Flex mih={40} gap="md" justify="space-between" align="center">
                <Box style={{ flex: 1 }}>
                  <IconAlignLeft style={{ marginLeft: "4px" }} />
                </Box>
                <Box
                  style={{
                    display: "flex",
                    alignItems: "center",
                    justify: "space-around",
                    gap: "xl",
                    marginRight: "4px",
                  }}
                >
                  <Box style={{ marginRight: "10px" }}>
                    <IconSearch size={`15px`} />
                  </Box>
                  <Box>
                    <Avatar
                      size="sm"
                      bg="white"
                      radius="md"
                      src="https://images.unsplash.com/photo-1535713875002-d1d0cf377fde?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=250&q=80"
                    />
                  </Box>
                </Box>
              </Flex>
            </Stack>
            {selectedCategory !== null && (
              <Box mb="xs" bg="#FFFFFF" style={{ cursor: "pointer" }}>
                <Flex
                  h={{ base: "auto", md: 100 }}
                  justify="center"
                  align="center"
                  direction={{ base: "column", md: "row" }}
                  gap="sm"
                >
                  {categories.map((category, index) => (
                    <Flex
                      key={index}
                      justify="center"
                      align="center"
                      direction="column"
                      onClick={() => handleCategoryClick(index)}
                    >
                      <Avatar
                        variant="filled"
                        radius="sm"
                        size="lg"
                        color="yellow"
                        src={category.imageSrc}
                      />
                      <Text size="xs">{category.name}</Text>
                    </Flex>
                  ))}
                  <Flex h={{ base: "auto", md: `100%` }}>
                    <Checkbox defaultChecked />
                  </Flex>
                </Flex>
              </Box>
            )}
            <Flex
              style={{ flex: 1, background: "var(--mantine-color-gray-2)" }}
            ></Flex>

            <Flex
              mih={50}
              bg="dark.9"
              justify="space-around"
              align="center"
              direction="row"
              wrap="wrap"
              style={{
                padding: "10px",
              }}
            >
              {blackicons.map((blackicon, index) => (
                <ActionIcon
                  bg="dark.9"
                  radius="md"
                  key={index}
                  variant={selectedBlackIcon === index ? "transparent" : ""}
                >
                  {blackicon.component}
                </ActionIcon>
              ))}
            </Flex>
          </Box>
          {/* This is the last Box */}
          <Box
            bg="dark.9"
            style={{
              width: "100%",
              height: "30px",
              borderRadius: "0px 0px 14px 14px",
            }}
          >
            <Center mt="xs">
              <Box
                bg="white"
                style={{
                  width: "50px",
                  height: "5px",
                  borderRadius: "20px",
                }}
              />
            </Center>
          </Box>
        </Box>
      </Center>
    </Stack>
  );
};

export default ResponsivePhone;
