import React from "react";
import { Stack, Text, ColorPicker, Flex, Slider, Input, Accordion } from "@mantine/core";

const Layout3 = ({
  onColorChange,
  boxBorderRadius,
  columns,
  onBorderRadiusChange, // Keep the rest of your props
}) => {
  return (
    <Stack bg="gray.3">
      <Accordion>
        <Accordion.Item value="box">
          <Accordion.Control bg="indigo.2">Box</Accordion.Control>
          <Accordion.Panel>
            {/* Other settings here */}
          </Accordion.Panel>
        </Accordion.Item>

        <Accordion.Item value="item">
          <Accordion.Control bg="indigo.2">Item Box</Accordion.Control>
          <Accordion.Panel>
            <Stack>
              <Text style={{ marginLeft: "1rem" }}>Number of Columns</Text>
              <Flex
                mih={50}
                bg="white"
                ml="xs"
                mr="xs"
                gap="md"
                justify="center"
                align="center"
                direction="row"
                wrap="wrap"
                style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
              >
                <Slider
                  value={columns}
                  onChange={(value) => onColumnsChange(value)}
                  min={1}
                  max={12} // Adjust the max value to your desired maximum number of columns
                  labelAlwaysOn
                  style={{ width: "300px" }}
                />
              </Flex>
            </Stack>
          </Accordion.Panel>
        </Accordion.Item>
      </Accordion>
    </Stack>
  );
};

export default Layout3;
