import React, { useState } from "react";
import { ColorPicker, Grid, Box, Text, Center, Flex, Slider } from "@mantine/core";

const Layout = () => {
  const [layout, setLayout] = useState({
    background_color: "#cdcdcd",
    box_background_color: "#FFE066",
    border_radius: 10,
    heading_text: "Hello",
    items_coloum: 3,
    category: "new",
    shadow: 2,
  });

  const updateProperty = (property, value) => {
    setLayout({ ...layout, [property]: value });
  };

  return (
    <Grid>
      <Grid.Col span={6}>
        <Box
          p={10}
          bg={layout.background_color}
          h={300}
          w={`100%`}
          style={{ borderRadius: layout.border_radius }}
        >
          <Box bg={layout.box_background_color} h={50} w={`100%`}>
            <Center>
              <Text>{layout.heading_text}</Text>
            </Center>
          </Box>
        </Box>
      </Grid.Col>
      <Grid.Col span={6}>
        <ColorPicker
          size="lg"
          format="rgba"
          onChange={(value) => updateProperty("background_color", value)}
        />
        <ColorPicker
          size="lg"
          format="rgba"
          onChange={(value) => updateProperty("box_background_color", value)}
        />

        <Flex
          mih={50}
          w={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          <Slider
            value={layout.border_radius}
            onChange={(value) => updateProperty("border_radius", value)}
            min={0}
            max={50}
            labelAlwaysOn
            style={{ width: "300px" }}
          />
        </Flex>
      </Grid.Col>
    </Grid>
  );
};

export default Layout;
