import React from "react";
import { useDisclosure } from "@mantine/hooks";
import { AppShell, Box, Burger, Button, Group, Skeleton } from "@mantine/core";
import {
  IconAlignLeft,
  IconSearch,
  IconBellFilled,
  IconX,
} from "@tabler/icons-react";

const AppShellDemo = () => {
  const [opened, { toggle }] = useDisclosure();
  return (
    <AppShell
      header={{ height: 50 }}
      navbar={{ width: 80, breakpoint: "sm", collapsed: { mobile: !opened } }}
      aside={{
        width: 300,
        breakpoint: "md",
        collapsed: { desktop: false, mobile: true },
      }}
      padding="md"
    >
      <AppShell.Header>
        <Group h="100%" px="md">
          <Burger opened={opened} onClick={toggle} hiddenFrom="sm" size="sm" />
          {/* <MantineLogo size={30} /> */}
        </Group>
      </AppShell.Header>
      <AppShell.Navbar p="md">
        Navbar
        {Array(15)
          .fill(0)
          .map((_, index) => (
            <Skeleton key={index} h={28} mt="sm" animate={false} />
          ))}
      </AppShell.Navbar>
      <AppShell.Main>
        Aside is hidden on on md breakpoint and cannot be opened when it is
        collapsed
      </AppShell.Main>
      <AppShell.Aside p="md">
        <Button fullWidth>Item</Button>
        <div>
      <Box
        sx={(theme) => ({
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          width: "100%",
          backgroundColor:
            theme.colorScheme === "dark"
              ? theme.colors.dark[6]
              : theme.colors.gray[0],
          textAlign: "center",
          padding: theme.spacing.xs,
          cursor: "pointer",
          marginTop: theme.spacing.xs,
          "&:hover": {
            backgroundColor:
              theme.colorScheme === "dark"
                ? theme.colors.dark[5]
                : theme.colors.gray[1],
          },
        })}
        onClick={handleClick}
      >
        <div className="flex justify-between">
          <IconAlignLeft className="mt-2" />
          <Autocomplete
            className={classes.search}
            placeholder="Search"
            data={[
              "React",
              "Angular",
              "Vue",
              "Next.js",
              "Riot.js",
              "Svelte",
              "Blitz.js",
            ]}
          />
          <div className="flex justify-between">
            <IconSearch className="mt-2 mr-1" />
            <div className="inline-block">
              <IconBellFilled className="mt-2" />
            </div>
          </div>
          
        </div>
      </Box>

      
    </div>
      </AppShell.Aside>
    </AppShell>
  );
};

export default AppShellDemo;
