import React, { useState } from "react";
import { useDisclosure } from "@mantine/hooks";
import {
  Box,
  Center,
  Stack,
  Flex,
  ActionIcon,
  Autocomplete,
  Drawer,
  rem,
  Avatar,
  Text,
  Button,
  Paper,
  Menu,
  LoadingOverlay,
  Divider,
} from "@mantine/core";
import {
  IconHome2,
  IconUser,
  IconMessageCircle,
  IconBookmark,
  IconAlignLeft,
  IconSearch,
  IconCircleXFilled,
  IconBell,
  IconExternalLink,
  IconCategory2,
  IconLayoutDashboard,
  IconShoppingCart,
  IconList,
  IconLogout,
  IconRotateClockwise,
  IconCircleX,
} from "@tabler/icons-react";

const ResponsivePhone = ({ openDrawer }) => {
  const [visible] = useDisclosure(false);
  {
    /* Is true loader is visible */
  }

  const [selectedBlackIcon, setSelectedBlackIcon] = useState(null);
  const [selectedItem, setSelectedItem] = useState(null);

  const bgColor = visible ? "blue" : "gray.2";

  const handleBlackIconClick = (index, component) => {
    console.log("Clicked icon :", component);
    setSelectedBlackIcon(index);
  };

  const blackicons = [
    { name: "IconHome2", component: <IconHome2 /> },
    { name: "IconMessageCircle", component: <IconMessageCircle /> },
    { name: "IconBookmark", component: <IconBookmark /> },
    { name: "IconUser", component: <IconUser /> },
  ];

  return (
    <Stack>
      <Center>
        <Box
          className="responsive-phone"
          pos="relative"
          style={{
            position: "relative",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            maxWidth: "250px",
            width: "250px",
            height: "500px",
            backgroundColor: "var(--mantine-color-gray-0)",
            border: "2px solid var(--mantine-color-dark-9)",
            borderRadius: "18px",
            boxShadow: "5px 5px 10px rgba(0, 0, 0, 0.5)",
          }}
        >
          {/* This is the first Box */}
          <Box
            bg="dark.9"
            style={{
              width: "100%",
              height: "30px",
              borderRadius: "14px 14px 0px 0px",
            }}
          >
            <Center mt="xs">
              <Box
                bg="white"
                style={{
                  width: "50px",
                  height: "5px",
                  borderRadius: "20px",
                }}
              />
            </Center>
          </Box>
          {/* This is Display Box */}
          <Box
            bg={bgColor}
            style={{
              flex: 1,
              width: "100%",
              position: "relative",
              border: "2px solid var(--mantine-color-dark-9)",
              display: "flex",
              flexDirection: "column",
            }}
          >
            {/* Loader */}
            <LoadingOverlay
              visible={visible}
              zIndex={1000}
              overlayProps={{
                radius: "0",
                bg: "blue", // Set the background color to blue
                opacity: 0.7, // Adjust the opacity to your preference
              }} // bg:"gray.3"
              loaderProps={{ color: "pink", type: "bars", size: "xs" }}
              style={{
                position: "absolute",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
              }}
            />
            {openDrawer && (
              <Box
                bg="white"
                style={{
                  position: "absolute",
                  top: 0,
                  bottom: 0,
                  right: 0,
                  left: 0,
                  width: "200px",
                  height: "432px",
                  zIndex: 9,
                  // borderRadius: "0 15px 15px 0px",
                  boxShadow: "2px 0px 5px rgba(0, 0, 0, 0.2)",
                }}
              >
                {/* Profile */}
                <Stack gap={2}>
                <Box mt={10} style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
  <Text ml={10} c={`gray.6`} size="md">
    Profile
  </Text>
  <IconCircleX />
</Box>
                  <Flex
                    ml={10}
                    style={{ display: "flex", alignItems: "center" }}
                  >
                    <Avatar src="https://pbs.twimg.com/profile_images/1674815862879178752/nTGMV1Eo_400x400.jpg" />
                    <Box style={{ marginLeft: "5px" }}>
                      <Text fw={500}>Bill Gates</Text>
                      <Text c={`gray.6`} size="xs">
                        billgates@gmail.com
                      </Text>
                    </Box>
                  </Flex>
                </Stack>
                {/* Menu */}
                <Stack h={250} gap={2}>
                  <Text
                    ml={10}
                    mt={10}
                    c={`gray.6`}
                    align="flex-left"
                    size="md"
                  >
                    Menu
                  </Text>
                  <Menu>
                    <Menu.Item
                      value="homepage"
                      onClick={() => setSelectedItem("homepage")}
                      style={{
                        backgroundColor:
                          selectedItem === "homepage"
                            ? "var(--mantine-color-gray-3)"
                            : "transparent",
                      }}
                      leftSection={
                        <IconHome2 style={{ width: "14px", height: "14px" }} />
                      }
                    >
                      Homepage
                    </Menu.Item>
                    <Menu.Item
                      value="category"
                      onClick={() => setSelectedItem("category")}
                      style={{
                        backgroundColor:
                          selectedItem === "category"
                            ? "var(--mantine-color-gray-3)"
                            : "transparent",
                      }}
                      leftSection={
                        <IconList style={{ width: "14px", height: "14px" }} />
                      }
                    >
                      Categoy Page
                    </Menu.Item>
                    <Menu.Item
                      value="cart"
                      onClick={() => setSelectedItem("cart")}
                      style={{
                        backgroundColor:
                          selectedItem === "cart"
                            ? "var(--mantine-color-gray-3)"
                            : "transparent",
                      }}
                      leftSection={
                        <IconShoppingCart
                          style={{ width: "14px", height: "14px" }}
                        />
                      }
                    >
                      Cart Page
                    </Menu.Item>
                    <Menu.Item
                      value="dashboard"
                      onClick={() => setSelectedItem("dashboard")}
                      style={{
                        backgroundColor:
                          selectedItem === "dashboard"
                            ? "var(--mantine-color-gray-3)"
                            : "transparent",
                      }}
                      leftSection={
                        <IconLayoutDashboard
                          style={{ width: "14px", height: "14px" }}
                        />
                      }
                    >
                      Dashboard Page
                    </Menu.Item>
                  </Menu>
                </Stack>
                <Divider />
                {/*  */}
                <Stack gap={2}>
                  <Menu>
                    <Menu.Item
                      value="logout"
                      onClick={() => setSelectedItem("logout")}
                      style={{
                        backgroundColor:
                          selectedItem === "logout"
                            ? "var(--mantine-color-gray-3)"
                            : "transparent",
                        alignItems: "center", // Center vertically
                        justifyContent: "center", // Center horizontally
                      }}
                      // leftSection={
                      //   <IconHome2 style={{ width: "14px", height: "14px" }} />
                      // }
                    >
                      <Box
                        style={{
                          display: "flex",
                          alignItems: "center",
                          justifyContent: "center",
                        }}
                      >
                        <IconLogout style={{ width: "14px", height: "14px" }} />
                        <Text size="md" style={{ marginLeft: "5px" }}>
                          Log out
                        </Text>
                      </Box>
                    </Menu.Item>
                    <Menu.Item
                      value="restart"
                      onClick={() => setSelectedItem("restart")}
                      style={{
                        backgroundColor:
                          selectedItem === "restart"
                            ? "var(--mantine-color-gray-3)"
                            : "transparent",
                        display: "flex",
                        alignItems: "center", // Center vertically
                        justifyContent: "center", // Center horizontally
                      }}
                    >
                      <Box
                        style={{
                          display: "flex",
                          alignItems: "center",
                          justifyContent: "center",
                        }}
                      >
                        <IconRotateClockwise
                          style={{ width: "14px", height: "14px" }}
                        />
                        <Text size="md" style={{ marginLeft: "5px" }}>
                          Restart
                        </Text>
                      </Box>
                    </Menu.Item>
                  </Menu>
                </Stack>
              </Box>
            )}
            <Flex
              mih={50}
              gap="xs"
              justify="center"
              align="center"
              direction="row"
              wrap="wrap"
              style={{
                borderBottom: "1px solid var(--mantine-color-dark-9)",
              }}
            >
              <IconAlignLeft />
              <Box
                bg="gray.1"
                p={3}
                c={`gray.4`}
                style={{
                  width: "150px",
                  backgroundColor: "var(--mantine-color-gray-0)",
                  border: "1px solid var(--mantine-color-gray-4)",
                  borderRadius: "15px",
                }}
              >
                <Flex
                  gap="xs"
                  justify="flex-start"
                  align="center"
                  direction="row"
                  wrap="wrap"
                >
                  <IconSearch
                    style={{ width: rem(16), height: rem(16) }}
                    stroke={1.5}
                  />
                  <Text>Search</Text>
                </Flex>
              </Box>
              <Avatar bg="var(--mantine-color-gray-0)" radius="xl">
                <IconBell />
              </Avatar>
            </Flex>
            <Flex
              style={{ flex: 1, background: "var(--mantine-color-gray-2)" }}
            >
              {/* <Drawer
                ml="510px"
                mt="80px"
                height={300}
                size="15%"
                opened={openDrawer}
                title="Authentication"
              >
              </Drawer> */}
              {/* Drawer content */}

              {/* custom Drawer */}
            </Flex>
            <Flex
              // pos="relative"
              mih={50}
              bg="dark.9"
              justify="space-around"
              align="center"
              direction="row"
              wrap="wrap"
              style={{
                padding: "10px",
              }}
            >
              {blackicons.map((blackicon, index) => (
                <ActionIcon
                  bg="dark.9"
                  radius="md"
                  key={index}
                  variant={selectedBlackIcon === index ? "transparent" : ""}
                  // aria-label={`Icon ${blackicon.component}`}
                  // onClick={() => handleBlackIconClick(index, blackicon)}
                >
                  {blackicon.component}
                </ActionIcon>
              ))}
            </Flex>
          </Box>
          {/* This is the last Box */}
          <Box
            bg="dark.9"
            style={{
              width: "100%",
              height: "30px",
              // '--radius': '0px 0px 14px 14px',
              // borderRadius: 'var(--radius)',
              borderRadius: "0px 0px 14px 14px",
            }}
          >
            <Center mt="xs">
              <Box
                bg="white"
                style={{
                  width: "50px",
                  height: "5px",
                  // '--radius': '20px'
                  // , borderRadius: 'var(--radius)',
                  borderRadius: "20px",
                }}
              />
            </Center>
          </Box>
        </Box>
      </Center>
    </Stack>
  );
};

export default ResponsivePhone;
