import React, { useState } from "react";
import {
  ColorPicker,
  Grid,
  Box,
  Text,
  Center,
  Flex,
  Slider,
  Input,
  Accordion,
} from "@mantine/core";

const Layout = () => {
  const [layout, setLayout] = useState({
    background_color: "#cdcdcd",
    box_background_color: "#FFE066",
    border_radius: 10,
    heading_text: "Hello",
    items_coloum: 3,
    items_border_radius: 5,
    category: "new",
    shadow: 2,
  });

  const updateProperty = (property, value) => {
    setLayout({ ...layout, [property]: value });
  };

  const columnArray = Array.from(
    { length: layout.items_coloum },
    (_, i) => i + 1
  );

  const onTextChange = (text) => {
    updateProperty("heading_text", text);
  };

  return (
    <Grid>
      <Grid.Col span={6}>
        <Box
          p={20}
          bg={layout.background_color}
          h={300}
          w={`100%`}
          style={{ borderRadius: layout.border_radius }}
        >
          <Box
            bg={layout.box_background_color}
            h={30}
            w={`100%`}
            justify="center"
            align="center"
          >
            <Text>{layout.heading_text}</Text>
          </Box>
          <Grid mt="xs" ml="xs" mr="xs" justify="center">
            {columnArray.map((colIndex) => (
              <Grid.Col span={1} key={colIndex}>
                <Box
                  bg="blue.1"
                  h={60}
                  style={{ borderRadius: layout.items_border_radius }}
                ></Box>
              </Grid.Col>
            ))}
          </Grid>
        </Box>
      </Grid.Col>
      <Grid.Col span={6}>
        <Text>Box Background</Text>
        <ColorPicker
          size="lg"
          format="rgba"
          onChange={(value) => updateProperty("background_color", value)}
        />

        <Text>Box Radius</Text>
        <Flex
          mih={50}
          w={280}
          mt="xs"
          bg="white"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          <Slider
            value={layout.border_radius}
            onChange={(value) => updateProperty("border_radius", value)}
            min={0}
            max={50}
            labelAlwaysOn
            style={{ width: "250px" }}
          />
        </Flex>

        <Accordion>
          <Accordion.Item value="header">
            <Accordion.Control bg="indigo.2">Heading Box</Accordion.Control>
            <Accordion.Panel>
              <Text>Heading Background</Text>
              <ColorPicker
                mt="xs"
                size="lg"
                format="rgba"
                onChange={(value) =>
                  updateProperty("box_background_color", value)
                }
              />
              <Text>Heading Text</Text>
              <Input
                type="text"
                value={layout.heading_text}
                onChange={(event) => {
                  onTextChange(event.target.value);
                }}
                w={280}
                mt="xs"
              />
            </Accordion.Panel>
          </Accordion.Item>
          <Accordion.Item value="item">
            <Accordion.Control bg="indigo.2">Item Box</Accordion.Control>
            <Accordion.Panel>
              <Text>Item Column</Text>
              <Flex
                mih={50}
                w={280}
                mt="xs"
                bg="white"
                mr="xs"
                gap="md"
                justify="center"
                align="center"
                direction="row"
                wrap="wrap"
                style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
              >
                <Slider
                  value={layout.items_coloum}
                  onChange={(value) => updateProperty("items_coloum", value)}
                  min={1}
                  max={12}
                  labelAlwaysOn
                  style={{ width: "250px" }}
                />
              </Flex>
              <Text>Item Box Radius</Text>
              <Flex
                mih={50}
                w={280}
                mt="xs"
                bg="white"
                mr="xs"
                gap="md"
                justify="center"
                align="center"
                direction="row"
                wrap="wrap"
                style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
              >
                <Slider
                  value={layout.items_border_radius}
                  onChange={(value) =>
                    updateProperty("items_border_radius", value)
                  }
                  min={0}
                  max={20}
                  labelAlwaysOn
                  style={{ width: "250px" }}
                />
              </Flex>
            </Accordion.Panel>
          </Accordion.Item>
        </Accordion>
      </Grid.Col>
    </Grid>
  );
};

export default Layout;
