import { Box, Center, Flex, Grid, Stack, Text } from "@mantine/core";
import React, { useState } from "react";

const Layout3 = () => {
  return (
    <Box bg="gray.4" h={200} style={{ borderRadius: "10px" }}>
      <Stack spacing={0}>
        <Box
          bg="lime.5"
          style={{ borderRadius: "10px 10px 0 0", cursor: "pointer" }}
          // onClick={toggleTextBoxSelection}
        >
          <Center>
            <Text>Test</Text>
          </Center>
        </Box>
        <Grid ml="xs" mr="xs">
          {items.map((item, index) => (
            <Grid.Col span={4} key={index}>
              <Box
                bg={selected === item ? "blue.1" : "white"}
                h={60}
                style={{ border: "1px solid #000000", cursor: "pointer" }}
                onClick={() => onItemSelect(item)}
              >
                <Center>{item}</Center>
              </Box>
            </Grid.Col>
          ))}
        </Grid>
      </Stack>
    </Box>
  )
}

export default Layout3
