import React, { useState } from "react";
import {
  Flex,
  Stack,
  Text,
  Container,
  Box,
  Accordion,
  Radio,
  Group,
  Center,
  ColorInput,
  Slider,
  Input,
  Button,
  ColorPicker,
  ActionIcon,
} from "@mantine/core";
import {
  IconCircleXFilled,
  IconAlignLeft,
  IconAlignCenter,
  IconAlignJustified,
  IconAlignRight,
  IconAdjustmentsHorizontal,
  IconBaselineDensityMedium,
  IconBaselineDensitySmall,
} from "@tabler/icons-react";

const Drawer = () => {
  const [value, setValue] = useState(1);
  const [iconVariant, setIconVariant] = useState("default");

  const handleChange = (newValue) => {
    setValue(newValue);
  };

  const handleIncrement = () => {
    const newValue = value + 1;
    if (newValue <= 100) {
      setValue(newValue);
    }
  };

  const toggleIconVariant = () => {
    // Toggle the icon variant between "default" and "outline"
    setIconVariant(iconVariant === "default" ? "outline" : "default");
  };

  return (
    <Box bg="gray.3">
      <Container
        fluid
        bg="indigo.6"
        style={{
          borderRadius: "5px",
          display: "flex",
          height: "40px",
          alignItems: "center",
          justifyContent: "space-between",
        }}
      >
        <Text
          color="white"
          style={{ display: "inline-block", justifyContent: "flex-center" }}
        >
          Drawer Icon
        </Text>
        <div style={{ display: "flex", justifyContent: "flex-end" }}>
          <IconCircleXFilled />
        </div>
      </Container>

      <Stack m="xs">
        <Text ml="xs">Change Icon</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          <ActionIcon
            variant={iconVariant}
            aria-label="Settings"
            onClick={toggleIconVariant} // Toggle the variant on click
          >
            <IconBaselineDensityMedium style={{ border: "1px solid #ccc" }} />
          </ActionIcon>
          <IconBaselineDensitySmall style={{ border: "1px solid #ccc" }} />
          <IconAlignCenter style={{ border: "1px solid #ccc" }} />
          <IconAlignJustified style={{ border: "1px solid #ccc" }} />
          <IconAlignRight style={{ border: "1px solid #ccc" }} />
        </Flex>
      </Stack>
    </Box>
  );
};

export default Drawer;
