import React from "react";
import { useDisclosure } from "@mantine/hooks";
import { AppShell, Burger, Group, Text } from "@mantine/core";
import {
    IconHomeCog,
    IconAppsFilled,
    IconDeviceDesktopAnalytics,
    IconUser,
    IconGardenCart,
    IconClipboardText,
    IconSettings,
    IconLogout,
    IconSwitchHorizontal,
  } from "@tabler/icons-react";

const AppShellDemo = () => {
  const [opened, { toggle }] = useDisclosure();

  const headerStyles = {
    backgroundColor: "var(--mantine-color-blue-4)", // Set the background color to indigo
    borderRadius: "0.5rem", // Apply rounded corners
    padding: "0.5rem", // Add padding to the header
  };

  const links = [
    { link: '/about', label: 'Features' },
    {
      link: '#1',
      label: 'Learn',
      links: [
        { link: '/docs', label: 'Documentation' },
        { link: '/resources', label: 'Resources' },
        { link: '/community', label: 'Community' },
        { link: '/blog', label: 'Blog' },
      ],
    },
    { link: '/about', label: 'About' },
    { link: '/pricing', label: 'Pricing' },
    {
      link: '#2',
      label: 'Support',
      links: [
        { link: '/faq', label: 'FAQ' },
        { link: '/demo', label: 'Book a demo' },
        { link: '/forums', label: 'Forums' },
      ],
    },
  ];
  const items = links.map((link) => {
    const menuItems = link.links?.map((item) => (
      <Menu.Item key={item.link}>{item.label}</Menu.Item>
    ));

    if (menuItems) {
        return (
          <Menu key={link.label} trigger="hover" transitionProps={{ exitDuration: 0 }} withinPortal>
            <Menu.Target>
              <a
                href={link.link}
                className={classes.link}
                onClick={(event) => event.preventDefault()}
              >
                <Center>
                  <span className={classes.linkLabel}>{link.label}</span>
                  <IconChevronDown size="0.9rem" stroke={1.5} />
                </Center>
              </a>
            </Menu.Target>
            <Menu.Dropdown>{menuItems}</Menu.Dropdown>
          </Menu>
        );
      }
  
      return (
        <a
          key={link.label}
          href={link.link}
          className={classes.link}
          onClick={(event) => event.preventDefault()}
        >
          {link.label}
        </a>
      );
    });

  return (
    <AppShell header={{ height: 60 }} padding="md">
      <div style={{ backgroundColor: "blue" }} className="rounded-lg p-2">
        <AppShell.Header style={headerStyles}>
          <Group h="50%" px="md">
            <Text>Them-1</Text>
          </Group>
          <Group h="50%" px="md">
          {/* {navItems.map((item, index) => (
            <Dropdown
              key={index}
              control={<Button size="sm" variant="light" leftIcon={item.icon}>{item.label}</Button>}
              position="bottom-start"
              arrowSize={5}
              arrowStyle={{ backgroundColor: "blue" }}
              withArrow
              zIndex={1000}
              gutter={10}
            >
              <Paper padding="sm">
                <ul>
                  {item.dropdown.map((subitem, subindex) => (
                    <li key={subindex}>
                      <MantineText>{subitem}</MantineText>
                    </li>
                  ))}
                </ul>
              </Paper>
            </Dropdown>
          ))} */}
          {items}

          </Group>

          {/* Burger */}
          <Burger opened={opened} onClick={toggle} hiddenFrom="sm" size="sm" />
        </AppShell.Header>
      </div>
    </AppShell>
  );
};

export default AppShellDemo;
